import React, { useState, useEffect } from 'react';
import clsx from 'clsx';
import PerfectScrollbar from 'react-perfect-scrollbar';
import { withStyles } from '@material-ui/styles';
import {
    Card,
    CardActions,
    CardContent,
    Checkbox,
    Table,
    TableBody,
    TableCell,
    TableHead,
    TableRow,
    Typography,
    TablePagination
} from '@material-ui/core';
import moment from 'moment';
import CheckIcon from '@material-ui/icons/Check';
import { getInitials } from 'helpers';
//import { ToolbarTable } from './components';
import styles from './styles';

const PresencesTable = props => {
    const { className, classes, presenceDetailClass } = props;
    const [selectedUsers, setSelectedUsers] = useState([]);
    const [rowsPerPage, setRowsPerPage] = useState(5);
    const [page, setPage] = useState(0);
    const { checkDateList, presenceList } = presenceDetailClass;
    const { dssv } = presenceDetailClass.class;
    const { dateList } = checkDateList;
    const presenceListMember = presenceList.presenceList;

    const handleSelectAll = event => {
        // let selectedUsers;
        // if (event.target.checked) {
        //     selectedUsers = users.map(user => user._id);
        // } else {
        //     selectedUsers = [];
        // }
        // setSelectedUsers(selectedUsers);
    };
    const handleSelectOne = (event, id) => {
        // const selectedIndex = selectedUsers.indexOf(id);
        // let newSelectedUsers = [];
        // if (selectedIndex === -1) {
        //     newSelectedUsers = newSelectedUsers.concat(selectedUsers, id);
        // } else if (selectedIndex === 0) {
        //     newSelectedUsers = newSelectedUsers.concat(selectedUsers.slice(1));
        // } else if (selectedIndex === selectedUsers.length - 1) {
        //     newSelectedUsers = newSelectedUsers.concat(
        //         selectedUsers.slice(0, -1)
        //     );
        // } else if (selectedIndex > 0) {
        //     newSelectedUsers = newSelectedUsers.concat(
        //         selectedUsers.slice(0, selectedIndex),
        //         selectedUsers.slice(selectedIndex + 1)
        //     );
        // }
        // setSelectedUsers(newSelectedUsers);
    };
    const handlePageChange = (event, page) => {
        setPage(page);
    };
    const handleRowsPerPageChange = event => {
        setRowsPerPage(+event.target.value);
        setPage(0);
    };

    return (
        <Card className={clsx(classes.root, className)}>
            {/* <ToolbarTable
                selectedUsers={selectedUsers}
                deleteUsers={deleteUsers}
            /> */}
            <CardContent className={classes.content}>
                <PerfectScrollbar>
                    <div className={classes.inner}>
                        <Table size="small">
                            <TableHead>
                                <TableRow>
                                    <TableCell
                                        rowSpan={2}
                                        padding="checkbox"
                                        className={classes.borderTable}
                                    >
                                        <Checkbox
                                            // checked={
                                            //     selectedUsers.length ===
                                            //     users.length
                                            // }
                                            color="primary"
                                            // indeterminate={
                                            //     selectedUsers.length > 0 &&
                                            //     selectedUsers.length <
                                            //         users.length
                                            // }
                                            onChange={handleSelectAll}
                                        />
                                    </TableCell>
                                    <TableCell
                                        rowSpan={2}
                                        align="center"
                                        className={classes.borderTable}
                                    >
                                        Member code
                                    </TableCell>
                                    <TableCell
                                        rowSpan={2}
                                        className={classes.borderTable}
                                    >
                                        Name
                                    </TableCell>
                                    <TableCell
                                        rowSpan={2}
                                        align="center"
                                        className={classes.borderTable}
                                    >
                                        Birth date
                                    </TableCell>
                                    <TableCell
                                        rowSpan={2}
                                        align="center"
                                        className={classes.borderTable}
                                    >
                                        Gender
                                    </TableCell>
                                    <TableCell
                                        className={classes.borderTable}
                                        colSpan={dateList && dateList.length}
                                        align="center"
                                    >
                                        Check date
                                    </TableCell>
                                </TableRow>
                                <TableRow>
                                    {
                                        dateList &&
                                        dateList.map(value => (
                                            <TableCell
                                                key={value._id}
                                                align="center"
                                                className={classes.borderTable}
                                            >
                                                {value.date}
                                            </TableCell>
                                        ))
                                    }
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {dssv &&  
                                    dssv.slice(
                                        page * rowsPerPage,
                                        page * rowsPerPage + rowsPerPage
                                    )
                                    .map(sv => (
                                        <TableRow
                                            key={sv._id}
                                            className={classes.tableRow}
                                            hover
                                            // selected={
                                            //     selectedUsers.indexOf(
                                            //         user._id
                                            //     ) !== -1
                                            // }
                                        >
                                            <TableCell
                                                padding="checkbox"
                                                className={classes.borderTable}
                                            >
                                                <Checkbox
                                                    // checked={
                                                    //     selectedUsers.indexOf(
                                                    //         user._id
                                                    //     ) !== -1
                                                    // }
                                                    color="primary"
                                                    // onChange={event =>
                                                    //     handleSelectOne(event, user._id)
                                                    // }
                                                    value="true"
                                                />
                                            </TableCell>
                                            <TableCell
                                                align="center"
                                                className={classes.borderTable}
                                            >
                                                <Typography variant="body1">
                                                    {sv.maSV}
                                                </Typography>
                                            </TableCell>
                                            <TableCell
                                                className={classes.borderTable}
                                            >
                                                {sv.tenSV}
                                            </TableCell>
                                            <TableCell
                                                align="center"
                                                className={classes.borderTable}
                                            >
                                                {sv.ngaysinh}
                                            </TableCell>
                                            <TableCell
                                                align="center"
                                                className={classes.borderTable}
                                            >
                                                {sv.gioitinh}
                                            </TableCell>
                                            {presenceListMember.map(value1 => {
                                                return dateList.map(value2 => {
                                                    if(sv.maSV === value1.memberCode && value1.checkDate === value2.date)
                                                    {
                                                        return (
                                                            <TableCell
                                                                key={value._id}
                                                                align="center"
                                                                className={classes.borderTable}
                                                            >
                                                                <CheckIcon />
                                                            </TableCell>
                                                        )
                                                    }
                                                    return (
                                                        <TableCell
                                                            key={value._id}
                                                            align="center"
                                                            className={classes.borderTable}
                                                        >
                                                        </TableCell>
                                                    ) 
                                                })
                                            })}
                                            
                                            {/* <TableCell
                                                align="center"
                                                className={classes.borderTable}
                                            >
                                                <CheckIcon />
                                            </TableCell>
                                            <TableCell
                                                align="center"
                                                className={classes.borderTable}
                                            >
                                                <CheckIcon />
                                            </TableCell> */}
                                        </TableRow>
                                    ))}
                            </TableBody>
                        </Table>
                    </div>
                </PerfectScrollbar>
            </CardContent>
            <CardActions className={classes.actions}>
                <TablePagination
                    component="div"
                    count={dssv ? dssv.length : 0}
                    onChangePage={handlePageChange}
                    onChangeRowsPerPage={handleRowsPerPageChange}
                    page={page}
                    rowsPerPage={rowsPerPage}
                    rowsPerPageOptions={[]}
                    backIconButtonProps={{
                        'aria-label': 'previous page'
                    }}
                    nextIconButtonProps={{
                        'aria-label': 'next page'
                    }}
                    labelRowsPerPage=""
                />
            </CardActions>
        </Card>
    );
};
export default withStyles(styles)(PresencesTable);
