import { GET_USERS } from './../constants/types';

const initialState = {
    users: [],
    userTotal: 0
};

export default function(state = initialState, action) {
    switch(action.type)
    {   
        case GET_USERS:
            return {
                ...state,
                users: action.payload.users,
                userTotal: action.payload.userTotal
            }
        default:
            return state;
    }
};
