import React, { useState, useEffect } from 'react';
import Dialog from '@material-ui/core/Dialog';
import {
    Card,
    CardHeader,
    CardContent,
    CardActions,
    Divider,
    Grid,
    Button,
    TextField,
    Radio,
    RadioGroup,
    FormControlLabel,
    FormControl,
    FormLabel
} from '@material-ui/core';
import { withStyles } from '@material-ui/core/styles';
import DateFnsUtils from '@date-io/date-fns';
import { MuiPickersUtilsProvider, DatePicker } from '@material-ui/pickers';
import moment from 'moment';

import { LoadingButton } from 'components';
import styles from './styles';

const AddMemberDialog = props => {
    const { open, handleCloseDialog, classes, addClassMemberById, classById, errors } = props;
    const [value, setValue] = useState({
        mssv: '',
        ten: '',
        gioitinh: 'male'
    });
    const [ngaysinh, setNgaysinh] = useState(new Date());
    const [loadingButton, setLoadingButton] = useState(false);
    const today = moment();

    useEffect(() => {
        setLoadingButton(false);
    }, [errors]);
    const handleDateChangeDate = date => {
        setNgaysinh(date);
    };
    const handleChange = e => {
        setValue({
            ...value,
            [e.target.name]: e.target.value
        });
    };
    const handleClose = () => {
        handleCloseDialog(false);
    };
    const handleClick = e => {
        e.preventDefault();
        setLoadingButton(true);
        const newMember = {};
        newMember.mssv = value.mssv;
        newMember.ten = value.ten;
        newMember.gioitinh = value.gioitinh
        newMember.ngaysinh = ngaysinh;
        addClassMemberById(classById._id, newMember);
    };
    return (
        <Dialog
            open={open}
            onClose={handleClose}
            aria-labelledby="draggable-dialog-title"
        >
            <div className={classes.root}>
                <Card>
                    <form autoComplete="off" noValidate>
                        <CardHeader title="Add member" />
                        <Divider />
                        <CardContent>
                            <Grid container spacing={1}>
                                <MuiPickersUtilsProvider utils={DateFnsUtils}>
                                    <Grid item md={6} xs={12}>
                                        <TextField
                                            fullWidth
                                            label="Member code"
                                            margin="dense"
                                            name="mssv"
                                            variant="outlined"
                                            required
                                            onChange={handleChange}
                                            error={errors.mssv ? true : false}
                                            helperText={
                                                errors.mssv ? errors.mssv : null
                                            }
                                        />
                                    </Grid>
                                    <Grid item md={6} xs={12}>
                                        <TextField
                                            fullWidth
                                            label="Member name"
                                            margin="dense"
                                            name="ten"
                                            required
                                            variant="outlined"
                                            onChange={handleChange}
                                            error={errors.ten ? true : false}
                                            helperText={
                                                errors.ten ? errors.ten : null
                                            }
                                        />
                                    </Grid>
                                    <Grid item md={6} xs={12}>
                                        <DatePicker
                                            inputVariant="outlined"
                                            label="Birth day"
                                            format="dd/MM/yyyy"
                                            onChange={handleDateChangeDate}
                                            fullWidth
                                            value={ngaysinh}
                                            maxDate={today}
                                            margin="dense"
                                        />
                                    </Grid>
                                    <Grid item md={6} xs={12}>
                                        <FormControl component="fieldset">
                                            <FormLabel component="legend">
                                                Gender
                                            </FormLabel>
                                            <RadioGroup
                                                aria-label="gioitinh"
                                                name="gioitinh"
                                                value={value.gioitinh}
                                                onChange={handleChange}
                                                row
                                            >
                                                <FormControlLabel
                                                    value="male"
                                                    control={
                                                        <Radio color="secondary" />
                                                    }
                                                    label="Male"
                                                    labelPlacement="start"
                                                />
                                                <FormControlLabel
                                                    value="female"
                                                    control={
                                                        <Radio color="secondary" />
                                                    }
                                                    label="Female"
                                                    labelPlacement="start"
                                                />
                                            </RadioGroup>
                                        </FormControl>
                                    </Grid>
                                </MuiPickersUtilsProvider>
                            </Grid>
                        </CardContent>
                        <Divider />
                        <CardActions>
                            <Grid
                                container
                                direction="row"
                                justify="flex-end"
                                alignItems="flex-end"
                            >
                                <Button
                                    type="submit"
                                    color="primary"
                                    onClick={handleClick}
                                    disabled={loadingButton}
                                >
                                    Save
                                    {loadingButton ? <LoadingButton /> : null}
                                </Button>
                                <Button onClick={handleClose} color="secondary">
                                    Cancel
                                </Button>
                            </Grid>
                        </CardActions>
                    </form>
                </Card>
            </div>
        </Dialog>
    );
};
export default withStyles(styles)(AddMemberDialog);
