import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import { withStyles } from '@material-ui/core/styles';
import { Grid, TablePagination } from '@material-ui/core';
import { getClassAssignes } from './../../actions/assignes';
import { getUserConditionStatusTrue } from './../../actions/users';
import { changeManagerPerson, removeManagerPerson } from './../../actions/actionClassDetail';
import { AssignesToolbar, CardClassAssignes } from './components';
import { LoadingCenter } from 'components';
import styles from './styles';

const Assignes = props => {
    const {
        classes,
        getClassAssignes,
        assignesClass,
        isLoading,
        changeManagerPerson,
        removeManagerPerson,
        getUserConditionStatusTrue
    } = props;
    const [rowsPerPage, setRowsPerPage] = useState(6);
    const [page, setPage] = useState(0);

    useEffect(() => {
        getClassAssignes();
    }, [getClassAssignes]);
    useEffect(() => {
        getUserConditionStatusTrue();
    }, [getUserConditionStatusTrue]);

    const handlePageChange = (event, page) => {
        setPage(page);
    };
    const handleRowsPerPageChange = event => {
        setRowsPerPage(+event.target.value);
        setPage(0);
    };

    if (isLoading) return <LoadingCenter />;
    return (
        <div className={classes.root}>
            <AssignesToolbar />
            <div className={classes.content}>
                <Grid container spacing={3}>
                    {assignesClass.length === 0
                        ? 'Assignes not found'
                        : assignesClass
                              .slice(
                                  page * rowsPerPage,
                                  page * rowsPerPage + rowsPerPage
                              )
                              .map(lop => (
                                  <Grid
                                      item
                                      lg={4}
                                      md={6}
                                      xs={12}
                                      key={lop._id}
                                  >
                                        <CardClassAssignes 
                                            lop={lop} 
                                            changeManagerPerson={changeManagerPerson}

                                        />
                                  </Grid>
                              ))}
                </Grid>
            </div>
            <div className={classes.pagination}>
                <TablePagination
                    component="div"
                    count={assignesClass.length}
                    onChangePage={handlePageChange}
                    onChangeRowsPerPage={handleRowsPerPageChange}
                    page={page}
                    rowsPerPage={rowsPerPage}
                    rowsPerPageOptions={[]}
                    backIconButtonProps={{
                        'aria-label': 'previous page'
                    }}
                    nextIconButtonProps={{
                        'aria-label': 'next page'
                    }}
                    labelRowsPerPage=""
                />
            </div>
        </div>
    );
};
const mapStateToProps = state => ({
    assignesClass: state.assignesClass,
    isLoading: state.isLoading.isLoading,
});
export default connect(
    mapStateToProps,
    { getClassAssignes, changeManagerPerson, removeManagerPerson, getUserConditionStatusTrue }
)(withStyles(styles)(Assignes));
