import React, { useState, useEffect } from 'react';
import clsx from 'clsx';
import PerfectScrollbar from 'react-perfect-scrollbar';
import { withStyles } from '@material-ui/styles';
import {
    Card,
    CardActions,
    CardContent,
    Checkbox,
    Table,
    TableBody,
    TableCell,
    TableHead,
    TableRow,
    Typography,
    TablePagination
} from '@material-ui/core';
import moment from 'moment';
import CheckIcon from '@material-ui/icons/Check';
import { getInitials } from 'helpers';
//import { ToolbarTable } from './components';
import styles from './styles';

const PresencesTable = props => {
    const { className, classes, presenceDetailClass } = props;
    const [selectedUsers, setSelectedUsers] = useState([]);
    const [rowsPerPage, setRowsPerPage] = useState(5);
    const [page, setPage] = useState(0);
    const { checkDateList, classById } = presenceDetailClass;

    const handleSelectAll = event => {
        // let selectedUsers;
        // if (event.target.checked) {
        //     selectedUsers = users.map(user => user._id);
        // } else {
        //     selectedUsers = [];
        // }
        // setSelectedUsers(selectedUsers);
    };
    const handleSelectOne = (event, id) => {
        // const selectedIndex = selectedUsers.indexOf(id);
        // let newSelectedUsers = [];
        // if (selectedIndex === -1) {
        //     newSelectedUsers = newSelectedUsers.concat(selectedUsers, id);
        // } else if (selectedIndex === 0) {
        //     newSelectedUsers = newSelectedUsers.concat(selectedUsers.slice(1));
        // } else if (selectedIndex === selectedUsers.length - 1) {
        //     newSelectedUsers = newSelectedUsers.concat(
        //         selectedUsers.slice(0, -1)
        //     );
        // } else if (selectedIndex > 0) {
        //     newSelectedUsers = newSelectedUsers.concat(
        //         selectedUsers.slice(0, selectedIndex),
        //         selectedUsers.slice(selectedIndex + 1)
        //     );
        // }
        // setSelectedUsers(newSelectedUsers);
    };
    const handlePageChange = (event, page) => {
        setPage(page);
    };
    const handleRowsPerPageChange = event => {
        setRowsPerPage(+event.target.value);
        setPage(0);
    };

    return (
        <Card className={clsx(classes.root, className)}>
            {/* <ToolbarTable
                selectedUsers={selectedUsers}
                deleteUsers={deleteUsers}
            /> */}
            <CardContent className={classes.content}>
                <PerfectScrollbar>
                    <div className={classes.inner}>
                        <Table size="small">
                            <TableHead>
                                <TableRow>
                                    <TableCell
                                        rowSpan={2}
                                        padding="checkbox"
                                        className={classes.borderTable}
                                    >
                                        <Checkbox
                                            checked={
                                                classById.dssv &&
                                                selectedUsers.length ===
                                                classById.dssv.length
                                            }
                                            color="primary"
                                            indeterminate={
                                                
                                                selectedUsers.length > 0 &&
                                                selectedUsers.length <
                                                classById.dssv.length
                                            }
                                            onChange={handleSelectAll}
                                        />
                                    </TableCell>
                                    <TableCell
                                        rowSpan={2}
                                        align="center"
                                        className={classes.borderTable}
                                    >
                                        Member code
                                    </TableCell>
                                    <TableCell
                                        rowSpan={2}
                                        className={classes.borderTable}
                                    >
                                        Name
                                    </TableCell>
                                    <TableCell
                                        rowSpan={2}
                                        align="center"
                                        className={classes.borderTable}
                                    >
                                        Birth date
                                    </TableCell>
                                    <TableCell
                                        rowSpan={2}
                                        align="center"
                                        className={classes.borderTable}
                                    >
                                        Gender
                                    </TableCell>
                                    {checkDateList && (
                                        <TableCell
                                            className={classes.borderTable}
                                            colSpan={
                                                checkDateList.dateList &&
                                                checkDateList.dateList.length
                                            }
                                            align="center"
                                        >
                                            Check date
                                        </TableCell>
                                    )}
                                </TableRow>
                                {checkDateList && (
                                    <TableRow>
                                        {checkDateList.dateList &&
                                            checkDateList.dateList.map(date => (
                                                <TableCell
                                                    key={date._id}
                                                    align="center"
                                                    className={
                                                        classes.borderTable
                                                    }
                                                >
                                                    {date.date}
                                                </TableCell>
                                            ))}
                                    </TableRow>
                                )}
                            </TableHead>
                            <TableBody>
                                {classById.dssv &&
                                    classById.dssv.slice(
                                        page * rowsPerPage,
                                        page * rowsPerPage + rowsPerPage
                                    )
                                    .map(sv => (
                                        <TableRow
                                            key={sv._id}
                                            className={classes.tableRow}
                                            hover
                                            selected={
                                                selectedUsers.indexOf(
                                                    sv._id
                                                ) !== -1
                                            }
                                        >
                                            <TableCell
                                                padding="checkbox"
                                                className={classes.borderTable}
                                            >
                                                <Checkbox
                                                    // checked={
                                                    //     selectedUsers.indexOf(
                                                    //         user._id
                                                    //     ) !== -1
                                                    // }
                                                    color="primary"
                                                    // onChange={event =>
                                                    //     handleSelectOne(event, user._id)
                                                    // }
                                                    value="true"
                                                />
                                            </TableCell>
                                            <TableCell
                                                align="center"
                                                className={classes.borderTable}
                                            >
                                                <Typography variant="body1">
                                                    {sv.maSV}
                                                </Typography>
                                            </TableCell>
                                            <TableCell
                                                className={classes.borderTable}
                                            >
                                                {sv.tenSV}
                                            </TableCell>
                                            <TableCell
                                                align="center"
                                                className={classes.borderTable}
                                            >
                                                {sv.ngaysinh}
                                            </TableCell>
                                            <TableCell
                                                align="center"
                                                className={classes.borderTable}
                                            >
                                                {sv.gioitinh}
                                            </TableCell>
                                            {checkDateList && checkDateList.dateList.map(
                                                date => {
                                                    if (
                                                        !sv.checkDate.some(
                                                            x => x === date.date
                                                        )
                                                    ) {
                                                        return (
                                                            <TableCell
                                                                key={date._id}
                                                                align="center"
                                                                className={
                                                                    classes.borderTable
                                                                }
                                                            ></TableCell>
                                                        );
                                                    }
                                                    return sv.checkDate.map(
                                                        (kd, index) => {
                                                            if (
                                                                date.date === kd
                                                            ) {
                                                                //console.log(kd)
                                                                return (
                                                                    <TableCell
                                                                        key={
                                                                            index
                                                                        }
                                                                        align="center"
                                                                        className={
                                                                            classes.borderTable
                                                                        }
                                                                    >
                                                                        <CheckIcon />
                                                                    </TableCell>
                                                                );
                                                            }
                                                        }
                                                    );
                                                }
                                            )}
                                        </TableRow>
                                    ))}
                            </TableBody>
                        </Table>
                    </div>
                </PerfectScrollbar>
            </CardContent>
            <CardActions className={classes.actions}>
                <TablePagination
                    component="div"
                    count={classById.dssv ? classById.dssv.length : 0}
                    onChangePage={handlePageChange}
                    onChangeRowsPerPage={handleRowsPerPageChange}
                    page={page}
                    rowsPerPage={rowsPerPage}
                    rowsPerPageOptions={[]}
                    backIconButtonProps={{
                        'aria-label': 'previous page'
                    }}
                    nextIconButtonProps={{
                        'aria-label': 'next page'
                    }}
                    labelRowsPerPage=""
                />
            </CardActions>
        </Card>
    );
};
export default withStyles(styles)(PresencesTable);
