import React, { useEffect, useState } from 'react';
import { withStyles } from '@material-ui/styles';
import { connect } from 'react-redux';

import { UsersToolbar, UsersTable } from './components';
import { getUsers, deleteUsers } from './../../actions/users';
import { LoadingCenter, Notifies } from 'components';
import styles from './styles';

const UserList = props => {
    const { classes, users, getUsers, deleteUsers, statusDeleteUser } = props;
    const [showStatus, setShowStatus] = useState(false);
    useEffect(() => {
        if(users.length === 0)
        {
            getUsers();
        }
        if(statusDeleteUser)
        {
            setShowStatus(true);
        }
    }, [getUsers, users, statusDeleteUser]);
    if(users.length === 0)
    { 
        return <LoadingCenter />;
    }
    return (
        <div className={classes.root}>
            {showStatus && <Notifies variant="success" message={statusDeleteUser} />}
            <UsersToolbar />
            <div className={classes.content}>
                <UsersTable 
                    users={users} 
                    deleteUsers={deleteUsers}
                />
            </div>
        </div>
    );
};

const mapStateToProps = state => ({
    users: state.users.users,
    statusDeleteUser: state.users.status
});
export default connect(mapStateToProps, { getUsers, deleteUsers })(withStyles(styles)(UserList));
