import React, { Component } from 'react';
import PropTypes from 'prop-types';
import clsx from 'clsx';
import moment from 'moment';
import { withStyles } from '@material-ui/styles';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import {
    Card,
    CardActions,
    CardContent,
    Avatar,
    Typography,
    Divider,
    Button,
    LinearProgress
} from '@material-ui/core';
import { URL_getImage } from './../../../../constants/types';
import { getInfoUser } from './../../../../actions/authentication';
import { uploadPicture } from './../../../../actions/account';
import styles from './styles';

class AccountProfile extends Component {
    constructor(props)
    {
        super(props);
        this.state = {
            avatar: ''
        };
    }      
    profileComplete = (user, isAuth) => {
        if (isAuth) {
            let complete = 0;
            if (user.firstName) {
                complete += 20;
            }
            if (user.lastName) {
                complete += 20;
            }
            if (user.sdt) {
                complete += 20;
            }
            if (user.city) {
                complete += 20;
            }
            if (user.avatar) {
                complete += 20;
            }
            return complete;
        } else {
            return 0;
        }
    };
    handleChange = (e, user) => {
        this.state.avatar = e.target.files[0];
        if (this.state.avatar) {
            this.props.uploadPicture(this.state.avatar, user._id);
        }
    };
    componentWillUpdate(nextProps, nextState)
    {
        if(nextProps.file !== this.props.file)
        {
            this.props.getInfoUser(this.props.user._id)
        }
    }
    render() {
        const {
            className,
            user,
            isAuth,
            getInfoUser,
            file,
            classes
        } = this.props;
        console.log('avatar render' +user.avatar)
        return (
            <Card className={clsx(classes.root, className)}>
                <CardContent>
                    <div className={classes.details}>
                        <div>
                            <Typography gutterBottom variant="h2">
                                {`${user.firstName} ${user.lastName}`}
                            </Typography>
                            <Typography
                                className={classes.locationText}
                                color="textSecondary"
                                variant="body1"
                            >
                                {user.city}, {user.country}
                            </Typography>
                            <Typography
                                className={classes.dateText}
                                color="textSecondary"
                                variant="body1"
                            >
                                {moment().format('hh:mm A')} ({'GTM-7'})
                            </Typography>
                        </div>
                        <Avatar
                            className={classes.avatar}
                            src={user.avatar ? `${URL_getImage}${user.avatar}` : `//www.gravatar.com/avatar/f8aef9003205946523250a062b54bbb6?s=200&r=pg&d=mm`}
                        />
                    </div>
                    <div className={classes.progress}>
                        <Typography variant="body1">
                            {`Profile Completeness: ${this.profileComplete(
                                user,
                                isAuth
                            )}%`}
                        </Typography>
                        <LinearProgress
                            value={this.profileComplete(user, isAuth)}
                            variant="determinate"
                        />
                    </div>
                </CardContent>
                <Divider />
                <CardActions>
                    <input
                        accept="image/*"
                        className={classes.input}
                        id="text-button-file"
                        multiple
                        type="file"
                        onChange={(e) => this.handleChange(e, user)}
                    />
                    <label htmlFor="text-button-file">
                        <Button
                            component="span"
                            className={classes.uploadButton}
                            color="primary"
                            variant="text"
                        >
                            Upload picture
                        </Button>
                    </label>
                    <Button variant="text">Remove picture</Button>
                </CardActions>
            </Card>
        );
    }
}
AccountProfile.propTypes = {
    className: PropTypes.string,
    auth: PropTypes.object,
    uploadPicture: PropTypes.func,
    file: PropTypes.string,
    errors: PropTypes.object
};
const mapStateToProps = state => ({
    user: state.auth.user,
    file: state.file,
    isAuth: state.auth.isAuthenticated
});
const mapDispatchToProps = dispatch => {
    return {
        getInfoUser: bindActionCreators(getInfoUser, dispatch),
        uploadPicture: bindActionCreators(uploadPicture, dispatch)
    };
};
export default connect(
    mapStateToProps,
    mapDispatchToProps
)(withStyles(styles)(AccountProfile));
