import React, { useEffect } from 'react';
import { withStyles } from '@material-ui/styles';
import { Grid } from '@material-ui/core';
import { connect } from 'react-redux';
import jwt_decode from 'jwt-decode';

import { getInfoAccount } from './../../actions/account';
import { updateInfoAccount } from './../../actions/actionAccount';
import { closeNotify } from './../../actions/notify';
import { AccountProfile, AccountDetails } from './components';
import { LoadingCenter } from 'components';
import { Notifies } from 'components';

import styles from './styles';

const Account = props => {
    const {
        classes,
        errors,
        isLoading,
        showNotify,
        getInfoAccount,
        account,
        actionAccount,
        updateInfoAccount,
        closeNotify
    } = props;
    const idAccount = jwt_decode(localStorage.jwtToken)._id;

    useEffect(() => {
        getInfoAccount(idAccount);
    }, [getInfoAccount, idAccount, actionAccount]);

    const setCloseNotify = () => {
        closeNotify();
    };

    if(isLoading) return <LoadingCenter />
    return (
        <div className={classes.root}>
            <Notifies
                variant={actionAccount.isSuccess ? 'success' : 'error'}
                message={actionAccount.message}
                openNotify={actionAccount.isSuccess === null ? false : showNotify}
                setCloseNotify={setCloseNotify}
            />
            <Grid container spacing={4}>
                <Grid item lg={4} md={6} xl={4} xs={12}>
                    <AccountProfile
                        errors={errors}
                        account={account}
                    />
                </Grid>
                <Grid item lg={8} md={6} xl={8} xs={12}>
                    <AccountDetails
                        account={account}
                        errors={errors}
                        getInfoAccount={getInfoAccount}
                        updateInfoAccount={updateInfoAccount}
                    />
                </Grid>
            </Grid>
        </div>
    );
};
const mapStateToProps = state => ({
    errors: state.errors,
    account: state.account,
    actionAccount: state.actionAccount,
    isLoading: state.isLoading.isLoading,
    showNotify: state.showNotify.isShow,
    //user: state.auth.user,
    //file: state.file,
    //isAuth: state.auth.isAuthenticated,
    //info: state.info.user,
    //statusUpdateInfo: state.info.status
});
export default connect(
    mapStateToProps,
    { getInfoAccount, updateInfoAccount, closeNotify }
)(withStyles(styles)(Account));
