import React, { useEffect } from 'react';
import { makeStyles } from '@material-ui/styles';
import { Grid } from '@material-ui/core';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';

import { AccountProfile, AccountDetails } from './components';
import { getCity } from './../../actions/city';
import { uploadPicture } from './../../actions/account';

const useStyles = makeStyles(theme => ({
    root: {
        padding: theme.spacing(4)
    }
}));

const Account = props => {
    const { auth, citys, getCity, uploadPicture, filePath, errors } = props;
    const classes = useStyles();
    useEffect(() => { getCity(); },[getCity]);

    return (
        <div className={classes.root}>
            <Grid container spacing={4}>
                <Grid item lg={4} md={6} xl={4} xs={12}>
                    <AccountProfile 
                        auth={auth}
                        uploadPicture={uploadPicture}
                        filePath={filePath}
                        errors={errors}
                    />
                </Grid>
                <Grid item lg={8} md={6} xl={8} xs={12}>
                    <AccountDetails 
                        auth={auth}
                        citys={citys}
                    />
                </Grid>
            </Grid>
        </div>
    );
};
Account.propTypes = {
    auth: PropTypes.object,
    citys: PropTypes.array,
    getCity: PropTypes.func,
    uploadPicture: PropTypes.func,
    filePath: PropTypes.string,
    errors: PropTypes.object
}
const mapStateToProps = state => ({
    auth: state.auth,
    citys: state.citys,
    filePath: state.filePath,
    errors: state.errors
});
export default connect(
    mapStateToProps,
    { getCity, uploadPicture }
)(Account);
