import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import clsx from 'clsx';
import moment from 'moment';
import { makeStyles } from '@material-ui/styles';
import {
    Card,
    CardActions,
    CardContent,
    Avatar,
    Typography,
    Divider,
    Button,
    LinearProgress
} from '@material-ui/core';
import { URL_getImage } from './../../../../constants/types';

const useStyles = makeStyles(theme => ({
    root: {},
    details: {
        display: 'flex'
    },
    avatar: {
        marginLeft: 'auto',
        height: 110,
        width: 100,
        flexShrink: 0,
        flexGrow: 0
    },
    progress: {
        marginTop: theme.spacing(2)
    },
    uploadButton: {
        marginRight: theme.spacing(2)
    },
    input: {
        display: 'none'
    }
}));

const AccountProfile = props => {
    const { className, auth, uploadPicture, file, errors } = props;
    const classes = useStyles();
    let [ avatar, setAvatar ] = useState(auth.user.avatar);
    useEffect(() => {
        setAvatar(file);
    }, avatar);

    const profileComplete = () => {
        if (auth.isAuthenticated) {
            let complete = 0;
            if (auth.user.firstName) {
                complete += 20;
            }
            if (auth.user.lastName) {
                complete += 20;
            }
            if (auth.user.sdt) {
                complete += 20;
            }
            if (auth.user.city) {
                complete += 20;
            }
            if (auth.user.avatar.slice(0, 18) !== '//www.gravatar.com') {
                complete += 20;
            }
            return complete;
        } else {
            return 0;
        }
    };
    const handleChange = e => {
        avatar = e.target.files[0];
        uploadPicture(avatar, auth.user.id);
    }
    console.log(file);
    return (
        <Card className={clsx(classes.root, className)}>
            <CardContent>
                <div className={classes.details}>
                    <div>
                        <Typography gutterBottom variant="h2">
                            {`${auth.user.firstName} ${auth.user.lastName}`}
                        </Typography>
                        <Typography
                            className={classes.locationText}
                            color="textSecondary"
                            variant="body1"
                        >
                            {auth.user.city}, {auth.user.country}
                        </Typography>
                        <Typography
                            className={classes.dateText}
                            color="textSecondary"
                            variant="body1"
                        >
                            {moment().format('hh:mm A')} ({'GTM-7'})
                        </Typography>
                    </div>
                    <Avatar className={classes.avatar} src={avatar} />
                </div>
                <div className={classes.progress}>
                    <Typography variant="body1">
                        {`Profile Completeness: ${profileComplete()}%`}
                    </Typography>
                    <LinearProgress
                        value={profileComplete()}
                        variant="determinate"
                    />
                </div>
            </CardContent>
            <Divider />
            <CardActions>
                <input
                    accept="image/*"
                    className={classes.input}
                    id="text-button-file"
                    multiple
                    type="file"
                    onChange={handleChange}
                />
                <label htmlFor="text-button-file">
                    <Button 
                        component="span" 
                        className={classes.uploadButton}
                        color="primary"
                        variant="text"
                    >
                        Upload picture
                    </Button>
                </label>
                <Button variant="text">Remove picture</Button>
            </CardActions>
        </Card>
    );
};

AccountProfile.propTypes = {
    className: PropTypes.string,
    auth: PropTypes.object,
    uploadPicture: PropTypes.func,
    file: PropTypes.string,
    errors: PropTypes.object
};
export default AccountProfile;
