import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import clsx from 'clsx';
import jwt_decode from 'jwt-decode';
import { withStyles, useTheme } from '@material-ui/styles';
import { useMediaQuery } from '@material-ui/core';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';

import { Sidebar, Topbar, Footer } from './components';
import { logout, getInfoUser } from './../../actions/authentication';
import styles from './styles';

const Main = props => {
    const { children, user, logout, history, isAuth, getInfoUser } = props;
    useEffect(() => {
        if(!localStorage.jwtToken)
        {
            window.location.href = 'sign-in';
        }
        const decode = jwt_decode(localStorage.jwtToken);
        getInfoUser(decode._id);
    },[]);
    console.log(isAuth);
    const classes = useStyles();
    const theme = useTheme();
    const isDesktop = useMediaQuery(theme.breakpoints.up('lg'), {
        defaultMatches: true
    });

    const [openSidebar, setOpenSidebar] = useState(false);

    const handleSidebarOpen = () => {
        setOpenSidebar(true);
    };

    const handleSidebarClose = () => {
        setOpenSidebar(false);
    };

    const shouldOpenSidebar = isDesktop ? true : openSidebar;

    return (
        <div
            className={clsx({
                [classes.root]: true,
                [classes.shiftContent]: isDesktop
            })}
        >
            <Topbar 
                onSidebarOpen={handleSidebarOpen}
                logout={logout}
            />
            <Sidebar
                onClose={handleSidebarClose}
                open={shouldOpenSidebar}
                variant={isDesktop ? 'persistent' : 'temporary'}
                logout={logout}
                user={user}
            />
            <main className={classes.content}>
                {children}
                <Footer />
            </main>
        </div>
    );
};

Main.propTypes = {
    children: PropTypes.node,
    user: PropTypes.object,
    logout: PropTypes.func
};
const mapStateToProps = state => ({
    user: state.auth.user,
    isAuth: state.auth.isAuthenticated
})
export default connect(mapStateToProps, { logout, getInfoUser })(withRouter(withStyles(styles)(Main)));
