import React from 'react';
import { withStyles } from '@material-ui/styles';
import { Grid } from '@material-ui/core';
import { connect } from 'react-redux';

import { Notifications, Password } from './components';
import styles from './styles';
import { updatePassword } from './../../actions/account';
import  { Notifies } from 'components';

const Settings = props => {
    const { classes, updatePassword, user, statusUpdateInfo, errors, info } = props;
    console.log(statusUpdateInfo);
    return (
        <div className={classes.root}>
            { statusUpdateInfo ? <Notifies variant="success" message={statusUpdateInfo} /> : null }
            <Grid container spacing={4}>
                <Grid item md={7} xs={12}>
                    <Notifications />
                </Grid>
                <Grid item md={5} xs={12}>
                    <Password 
                        user={user}
                        updatePassword={updatePassword}
                        errors={errors}
                        info={info}
                        statusUpdateInfo={statusUpdateInfo}
                    />
                </Grid>
            </Grid>
        </div>
    );
};
const mapStateToProps = state => ({
    statusUpdateInfo: state.info.status,
    user: state.auth.user,
    info: state.info.user,
    errors: state.errors
});
export default connect(mapStateToProps, { updatePassword })(withStyles(styles)(Settings));
