import React from 'react';
import clsx from 'clsx';
import { withStyles } from '@material-ui/styles';
import { Link as RouterLink } from 'react-router-dom';
import {
    Card,
    CardHeader,
    Avatar,
    Typography,
    CardContent,
    Divider,
    CardActions,
    Button,
    Grid,
    Link
} from '@material-ui/core';
import moment from 'moment';
import { URI } from './../../../../constants/types';
import styles from './styles';

const CardClassAssignes = props => {
    const { classes, className, lop } = props;

    return (
        <Card className={clsx(classes.root, className)}>
            <CardHeader
                avatar={
                    <Avatar
                        src={
                            lop.hinhdaidien
                                ? `${URI}/getAvatar/${lop.hinhdaidien}`
                                : 'http://www.gravatar.com/avatar/f8aef9003205946523250a062b54bbb6?s=200&r=pg&d=retro'
                        }
                    />
                }
                title={<Typography variant="h5">{lop.tenlop}</Typography>}
                subheader={
                    <Typography variant="body2">
                        by{' '}
                        <span style={{ fontWeight: 'bold', fontSize: 15 }}>
                            {lop.tenUser}
                        </span>{' '}
                        | {`Updated ${moment(lop.updatedAt).fromNow()}`}
                    </Typography>
                }
            />
            <CardContent>
                <div className={classes.content}>
                    <Typography variant="body1" color="textSecondary">
                        {lop.mota}
                    </Typography>
                </div>
            </CardContent>
            <Divider />
            <div className={classes.info}>
                <Grid container justify="space-between">
                    <Grid item>
                        <Typography variant="h5" color="textPrimary">
                            {lop.dssv.length}
                        </Typography>
                        <Typography variant="body1" color="textSecondary">
                            Total member
                        </Typography>
                    </Grid>
                    <Grid item>
                        <Typography variant="h5" color="textPrimary">
                            {moment(lop.thoigianketthuc).fromNow()}
                        </Typography>
                        <Typography variant="body1" color="textSecondary">
                            End time
                        </Typography>
                    </Grid>
                    <Grid item>
                        <Typography variant="h5" color="textPrimary">
                            {lop.managed ? 'Managed' : 'Not yet managed'}
                        </Typography>
                        <Typography variant="body1" color="textSecondary" align='center'>
                            Status
                        </Typography>
                    </Grid>
                </Grid>
            </div>
            <CardActions>
                <Grid container justify="space-between" alignItems="center">
                    <Grid item>
                        <Button
                            variant="text"
                            //className={classes.changeButton}
                            color="primary"
                        >
                            Change
                        </Button>
                    </Grid>
                    <Grid item>
                        <Link
                            color="primary"
                            component={RouterLink}
                            to={`/classes/classDetail/${lop._id}`}
                            underline="none"
                            variant="button"
                        >
                            Clearn more
                        </Link>
                    </Grid>
                    <Grid item>
                        <Button variant="text" color="secondary">
                            Remove
                        </Button>
                    </Grid>
                </Grid>
            </CardActions>
        </Card>
    );
};
export default withStyles(styles)(CardClassAssignes);
