import React from 'react';
import clsx from 'clsx';
import { withStyles } from '@material-ui/styles';
import { Card, CardContent, Grid, Typography, Avatar } from '@material-ui/core';
import ArrowDownwardIcon from '@material-ui/icons/ArrowDownward';
import ArrowUpwardIcon from '@material-ui/icons/ArrowUpward';
import ClassIcon from '@material-ui/icons/Class';
import styles from './styles';

const handling = statistical => {
    const currentMonth = statistical.dataClassMonthCurrent.length;
    const lastMonth = statistical.dataClassMonthLast.length;
    let percentMonthCurrent = (currentMonth / (currentMonth + lastMonth)) * 100;
    let percentMonthLast = (lastMonth / (lastMonth + currentMonth)) * 100;
    if (percentMonthCurrent > percentMonthLast) {
        return true;
    }
    return false;
};
const count = statistical => {
    const currentMonth = statistical.dataClassMonthCurrent.length;
    const lastMonth = statistical.dataClassMonthLast.length;
    let percentMonthCurrent = (currentMonth / (currentMonth + lastMonth)) * 100;
    let percentMonthLast = (lastMonth / (lastMonth + currentMonth)) * 100;

    const n = parseFloat(percentMonthCurrent);
    percentMonthCurrent = Math.round(n * 1000) / 1000;

    const m = parseFloat(percentMonthLast);
    percentMonthLast = Math.round(m * 1000) / 1000;

    if(percentMonthCurrent > percentMonthLast)
    {
        return percentMonthCurrent - percentMonthLast;
    }
    else
    {
        return percentMonthLast - percentMonthCurrent;
    }
}
const TotalClasses = props => {
    const { className, classes, statistical } = props;
    console.log(statistical);

    return (
        <Card className={clsx(classes.root, className)}>
            <CardContent>
                <Grid container justify="space-between">
                    <Grid item>
                        <Typography
                            className={classes.title}
                            color="textSecondary"
                            gutterBottom
                            variant="body2"
                        >
                            TOTAL CLASSES
                        </Typography>
                        <Typography variant="h3">
                            {statistical.dataClassMonthCurrent.length}
                        </Typography>
                    </Grid>
                    <Grid item>
                        <Avatar className={classes.avatar}>
                            <ClassIcon className={classes.icon} />
                        </Avatar>
                    </Grid>
                </Grid>
                <div className={classes.difference}>
                    {handling(statistical) ? (
                        <ArrowUpwardIcon
                            className={classes.differenceIconSuccess}
                        />
                    ) : (
                        <ArrowDownwardIcon className={classes.differenceIcon} />
                    )}
                    <Typography
                        className={
                            handling(statistical)
                                ? classes.differenceValueSuccess
                                : classes.differenceValueSuccess
                        }
                        variant="body2"
                    >
                        {count(statistical)}
                    </Typography>
                    <Typography className={classes.caption} variant="caption">
                        Since last month
                    </Typography>
                </div>
            </CardContent>
        </Card>
    );
};
export default withStyles(styles)(TotalClasses);
