import React, { useState } from 'react';
import PropTypes from 'prop-types';
import clsx from 'clsx';
import moment from 'moment';
import { makeStyles } from '@material-ui/styles';
import {
    Card,
    CardActions,
    CardContent,
    Avatar,
    Typography,
    Divider,
    Button,
    LinearProgress
} from '@material-ui/core';
import { Upload, Button as ButtonAntd, Icon } from 'antd';


const useStyles = makeStyles(theme => ({
    root: {},
    details: {
        display: 'flex'
    },
    avatar: {
        marginLeft: 'auto',
        height: 110,
        width: 100,
        flexShrink: 0,
        flexGrow: 0
    },
    progress: {
        marginTop: theme.spacing(2)
    },
    uploadButton: {
        marginRight: theme.spacing(2)
    }
}));

const AccountProfile = props => {
    const { className, auth } = props;
    const classes = useStyles();
    const [fileList, setFileList] = useState([]);
    const profileComplete = () => {
        if (auth.isAuthenticated) {
            let complete = 0;
            if (auth.user.firstName) {
                complete += 20;
            }
            if (auth.user.lastName) {
                complete += 20;
            }
            if (auth.user.sdt) {
                complete += 20;
            }
            if (auth.user.city) {
                complete += 20;
            }
            if (auth.user.avatar.slice(0, 18) !== '//www.gravatar.com') {
                complete += 20;
            }
            return complete;
        } else {
            return 0;
        }
    };
    const handleChange = info => {
        let fileList = [...info.fileList];
        // 1. Limit the number of uploaded files
        // Only to show two recent uploaded files, and old ones will be replaced by the new
        fileList = fileList.slice(-1);

        // 2. Read from response and show file link
        console.log(fileList);
        fileList = fileList.map(file => {
            if (file.response) {
                // Component will show file.url as link
                file.url = file.response.url;
            }
            return file;
        });
        setFileList(fileList);
    };
    const pro = {
        action: 'http://localhost:4000/api/getFile',
        onChange: handleChange,
        multiple: true
    };
    return (
        <Card className={clsx(classes.root, className)}>
            <CardContent>
                <div className={classes.details}>
                    <div>
                        <Typography gutterBottom variant="h2">
                            {`${auth.user.firstName} ${auth.user.lastName}`}
                        </Typography>
                        <Typography
                            className={classes.locationText}
                            color="textSecondary"
                            variant="body1"
                        >
                            {auth.user.city}, {auth.user.country}
                        </Typography>
                        <Typography
                            className={classes.dateText}
                            color="textSecondary"
                            variant="body1"
                        >
                            {moment().format('hh:mm A')} ({'GTM-7'})
                        </Typography>
                    </div>
                    <Avatar className={classes.avatar} src={auth.user.avatar} />
                </div>
                <div className={classes.progress}>
                    <Typography variant="body1">
                        {`Profile Completeness: ${profileComplete()}%`}
                    </Typography>
                    <LinearProgress
                        value={profileComplete()}
                        variant="determinate"
                    />
                </div>
            </CardContent>
            <Divider />
            <CardActions>
                <Upload {...pro} fileList={fileList}>
                    <ButtonAntd>
                        <Icon type="upload" /> Upload picture
                    </ButtonAntd>
                </Upload>
                <Button variant="text">Remove picture</Button>
            </CardActions>
        </Card>
    );
};

AccountProfile.propTypes = {
    className: PropTypes.string,
    auth: PropTypes.object
};
export default AccountProfile;
