import React, { useEffect } from 'react';
import { withStyles } from '@material-ui/styles';
import { Grid } from '@material-ui/core';
import { connect } from 'react-redux';
import jwt_decode from 'jwt-decode';

import { AccountProfile, AccountDetails } from './components';
import { LoadingCenter } from './../../components';
//import { getInfoUser } from './../../actions/authentication';
import { getInfoAccount } from './../../actions/account';
import { getCity } from './../../actions/city';
import { Notifies } from './../../components';
import styles from './styles';

const Account = props => {
    const {
        classes,
        errors,
        isLoading,
        showNotify,
        getInfoAccount,
        account,
        citys
    } = props;

    useEffect(() => {
        getInfoAccount();
    });

    if(isLoading) return <LoadingCenter />
    return (
        <div className={classes.root}>
            {/* <Notifies 
                openNotify={openNotify}
                setCloseNotify={params => this.setCloseNotify(params)}
                variant="success" 
                message={statusUpdateInfo} 
            /> */}
            <Grid container spacing={4}>
                <Grid item lg={4} md={6} xl={4} xs={12}>
                    {/* <AccountProfile
                        errors={errors}
                        user={user}
                        file={file}
                        isAuth={isAuth}
                        getInfoUser={getInfoUser}
                        uploadPicture={uploadPicture}
                        removePicture={removePicture}
                    /> */}
                </Grid>
                <Grid item lg={8} md={6} xl={8} xs={12}>
                    <AccountDetails
                        account={account}
                        //citys={citys}
                        errors={errors}
                        getInfoAccount={getInfoAccount}
                    />
                </Grid>
            </Grid>
        </div>
    );
};
const mapStateToProps = state => ({
    errors: state.errors,
    account: state.account,
    isLoading: state.isLoading.isLoading,
    showNotify: state.showNotify.isShow,
    //user: state.auth.user,
    //file: state.file,
    //isAuth: state.auth.isAuthenticated,
    citys: state.citys,
    //info: state.info.user,
    //statusUpdateInfo: state.info.status
});
export default connect(
    mapStateToProps,
    { getInfoAccount, getCity }
)(withStyles(styles)(Account));
