import React from 'react';
import palette from 'theme/palette';
import PresentIcon from '@material-ui/icons/Spellcheck';
import ResonIcon from '@material-ui/icons/Warning';
import AbsentIcon from '@material-ui/icons/CancelPresentation';
import moment from 'moment';

export const fillData = (dataPresences, date, index) => {
    console.log(date);

    let data, statusPresences;
    const { checkDates, classPresences } = dataPresences;
    let listDataPresent = 0;
    let listDataReason = 0;
    let listDataAbsent = 0;
    let total = 0;
    if (!index) {
        if (classPresences.length !== 0) {
            const monthCurrent = moment().format('MM');
            classPresences.forEach(lop => {
                checkDates.forEach(item => {
                    if (item.idClass === lop._id) {
                        total += item.dateList.length * lop.dssv.length;
                    }
                });

                lop.dssv.forEach(sv => {
                    sv.checkDate.forEach(date => {
                        if (
                            moment(date.date, 'DD/MM/YYYY').format('MM') ===
                                monthCurrent &&
                            date.status
                        ) {
                            listDataPresent++;
                        } else if (
                            moment(date.date, 'DD/MM/YYYY').format('MM') ===
                                monthCurrent &&
                            !date.status
                        ) {
                            listDataReason++;
                        }
                    });
                });
            });
            listDataAbsent = total - (listDataPresent + listDataReason);

            data = {
                datasets: [
                    {
                        data: [
                            Math.round(
                                (listDataPresent /
                                    (listDataPresent +
                                        listDataAbsent +
                                        listDataReason)) *
                                    100
                            ),
                            Math.round(
                                (listDataAbsent /
                                    (listDataPresent +
                                        listDataAbsent +
                                        listDataReason)) *
                                    100
                            ),
                            Math.round(
                                (listDataReason /
                                    (listDataPresent +
                                        listDataAbsent +
                                        listDataReason)) *
                                    100
                            )
                        ],
                        backgroundColor: [
                            palette.primary.main,
                            palette.error.main,
                            palette.warning.main
                        ],
                        borderWidth: 5,
                        borderColor: palette.white,
                        hoverBorderColor: palette.white
                    }
                ],
                labels: ['Present', 'Absent', 'Reason']
            };
            statusPresences = [
                {
                    title: 'Present',
                    value: `${Math.round(
                        (listDataPresent /
                            (listDataPresent +
                                listDataAbsent +
                                listDataReason)) *
                            100
                    )}`,
                    icon: <PresentIcon />,
                    color: palette.primary.main
                },
                {
                    title: 'Absent',
                    value: `${Math.round(
                        (listDataAbsent /
                            (listDataPresent +
                                listDataAbsent +
                                listDataReason)) *
                            100
                    )}`,
                    icon: <AbsentIcon />,
                    color: palette.error.main
                },
                {
                    title: 'Reson',
                    value: `${Math.round(
                        (listDataReason /
                            (listDataPresent +
                                listDataAbsent +
                                listDataReason)) *
                            100
                    )}`,
                    icon: <ResonIcon />,
                    color: palette.warning.main
                }
            ];
        } else {
            data = null;
        }
    }
    if(index === 0)
    {
        console.log(checkDates)
        const monthCurrent = moment(date).format('MM');
        classPresences.forEach(lop => {
            checkDates.forEach(item => {
                if (item.idClass === lop._id) {
                    item.dateList.forEach(ngay => {
                        if(moment(ngay.date, 'DD/MM/YYYY').format('MM') === monthCurrent)    
                        {
                            total++;
                        }
                    })
                    //total += item.dateList.length * lop.dssv.length;
                }
            });
            console.log(total)
            lop.dssv.forEach(sv => {
                sv.checkDate.forEach(date => {
                    if (
                        moment(date.date, 'DD/MM/YYYY').format('MM') ===
                            monthCurrent &&
                        date.status
                    ) {
                        listDataPresent++;
                    } else if (
                        moment(date.date, 'DD/MM/YYYY').format('MM') ===
                            monthCurrent &&
                        !date.status
                    ) {
                        listDataReason++;
                    }
                });
            });
        });
        listDataAbsent = total - (listDataPresent + listDataReason);

        console.log(listDataPresent);
        data = {
            datasets: [
                {
                    data: [
                        Math.round(
                            (listDataPresent /
                                (listDataPresent +
                                    listDataAbsent +
                                    listDataReason)) *
                                100
                        ),
                        Math.round(
                            (listDataAbsent /
                                (listDataPresent +
                                    listDataAbsent +
                                    listDataReason)) *
                                100
                        ),
                        Math.round(
                            (listDataReason /
                                (listDataPresent +
                                    listDataAbsent +
                                    listDataReason)) *
                                100
                        )
                    ],
                    backgroundColor: [
                        palette.primary.main,
                        palette.error.main,
                        palette.warning.main
                    ],
                    borderWidth: 5,
                    borderColor: palette.white,
                    hoverBorderColor: palette.white
                }
            ],
            labels: ['Present', 'Absent', 'Reason']
        };
        statusPresences = [
            {
                title: 'Present',
                value: `${Math.round(
                    (listDataPresent /
                        (listDataPresent +
                            listDataAbsent +
                            listDataReason)) *
                        100
                )}`,
                icon: <PresentIcon />,
                color: palette.primary.main
            },
            {
                title: 'Absent',
                value: `${Math.round(
                    (listDataAbsent /
                        (listDataPresent +
                            listDataAbsent +
                            listDataReason)) *
                        100
                )}`,
                icon: <AbsentIcon />,
                color: palette.error.main
            },
            {
                title: 'Reson',
                value: `${Math.round(
                    (listDataReason /
                        (listDataPresent +
                            listDataAbsent +
                            listDataReason)) *
                        100
                )}`,
                icon: <ResonIcon />,
                color: palette.warning.main
            }
        ];
    }
    // dataClass.forEach(lop => {
    //     presences.presenceList.forEach(kd => {

    //     });
    // });

    //const start = moment(new Date()).startOf('isoWeek');

    // presences.forEach(e => {
    //     e.presenceList.forEach(item => {
    //         if(moment(item.checkDate, 'DD/MM/YYYY').isSame(start, 'isoWeek') && item.status)
    //         {
    //             listDataPresent.push(item);
    //         }
    //         else if(moment(item.checkDate, 'DD/MM/YYYY').isSame(start, 'isoWeek') && !item.status)
    //         {
    //             listDataReason.push(item);
    //         }
    //     });
    // });

    // console.log(listDataPresent.length);
    // console.log(listDataReason.length);

    //console.log(moment().isBetween(start, end));

    return {
        data,
        statusPresences
    };
};
export const options = {
    legend: {
        display: false
    },
    responsive: true,
    maintainAspectRatio: false,
    layout: { padding: 0 },
    tooltips: {
        enabled: true,
        mode: 'index',
        intersect: false,
        borderWidth: 1,
        borderColor: palette.divider,
        backgroundColor: palette.white,
        titleFontColor: palette.text.primary,
        bodyFontColor: palette.text.secondary,
        footerFontColor: palette.text.secondary
    }
};
