import { GET_CURRENT_ACCOUNT, FETCHING_DATA, FETCH_DATA_SUCCESS } from './../constants/types';

export const getInfoUser = idAccount => dispatch => {
    dispatch({
        type: FETCHING_DATA
    });
    callAPI(`/getInfoUser/?id=${idAccount}`, 'GET', null)
        .then(res => {
            dispatch({
                type: GET_CURRENT_ACCOUNT,
                payload: res.data
            });
            dispatch({
                type: FETCH_DATA_SUCCESS
            });
        });
};
