import { UPLOAD_PICTURE, GET_ERRORS } from './../constants/types';
import callAPI from './../common/callApi';

export const uploadPicture = file => dispatch => {
    const formData = new FormData();
    formData.append('avatar', file);
    // const config = {
    //     headers: {
    //         'content-type': 'multipart/form-data'
    //     }
    // };
    callAPI('/updateAvatar', 'POST', formData)
        .then(res => {
            dispatch({
                type: UPLOAD_PICTURE,
                payload: res.data.filePath
            });
        })
        .catch(err => {
            dispatch({
                type: GET_ERRORS,
                payload: err.response.data
            });
        });
}
