import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import { withStyles } from '@material-ui/styles';
import { Grid } from '@material-ui/core';
import {
    Budget,
    TotalUsers,
    TasksProgress,
    TotalProfit,
    LatestSales,
    UsersByDevice,
    LatestClasses,
    LatestOrders
} from './components';
import { closeNotify } from './../../actions/notify';
import { getClassLatest } from './../../actions/dashboard';
import { deleteClass } from './../../actions/actionClass';
import { Notifies, LoadingCenter } from 'components';
import styles from './styles';

const Dashboard = props => {
    const {
        classes,
        auth,
        showNotify,
        closeNotify,
        classLatest,
        getClassLatest,
        isLoading,
        deleteClass
    } = props;

    useEffect(() => {
        getClassLatest();
    }, [getClassLatest]);

    const setCloseNotify = () => {
        closeNotify();
    };

    if (isLoading) return <LoadingCenter />;
    return (
        <div className={classes.root}>
            <Notifies
                variant={auth.isSuccess ? 'success' : 'error'}
                message={auth.message}
                openNotify={auth.isSuccess === null ? false : showNotify}
                setCloseNotify={setCloseNotify}
            />
            <Grid container spacing={4}>
                <Grid item lg={3} sm={6} xl={3} xs={12}>
                    <Budget />
                </Grid>
                <Grid item lg={3} sm={6} xl={3} xs={12}>
                    <TotalUsers />
                </Grid>
                <Grid item lg={3} sm={6} xl={3} xs={12}>
                    <TasksProgress />
                </Grid>
                <Grid item lg={3} sm={6} xl={3} xs={12}>
                    <TotalProfit />
                </Grid>
                <Grid item lg={8} md={12} xl={9} xs={12}>
                    <LatestSales />
                </Grid>
                <Grid item lg={4} md={6} xl={3} xs={12}>
                    <UsersByDevice />
                </Grid>
                <Grid item lg={4} md={6} xl={3} xs={12}>
                    <LatestClasses 
                        classLatest={classLatest} 
                        deleteClass={deleteClass}
                    />
                </Grid>
                <Grid item lg={8} md={12} xl={9} xs={12}>
                    <LatestOrders />
                </Grid>
            </Grid>
        </div>
    );
};
const mapStateToProps = state => ({
    auth: state.auth,
    showNotify: state.showNotify.isShow,
    classLatest: state.classes.classes,
    isLoading: state.isLoading.isLoading
});
export default connect(
    mapStateToProps,
    { closeNotify, getClassLatest, deleteClass }
)(withStyles(styles)(Dashboard));
