import React, { useState } from 'react';
import { Pie } from 'react-chartjs-2';
import clsx from 'clsx';
import { withStyles, useTheme } from '@material-ui/styles';
import {
    Card,
    CardHeader,
    CardContent,
    IconButton,
    Divider,
    Typography,
    Menu,
    MenuItem
} from '@material-ui/core';
import LaptopMacIcon from '@material-ui/icons/LaptopMac';
import PhoneIphoneIcon from '@material-ui/icons/PhoneIphone';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import TabletMacIcon from '@material-ui/icons/TabletMac';
import styles from './styles';

const MemberByPresence = props => {
    const { className, classes, ...rest } = props;
    const [anchorEl, setAnchorEl] = useState(null);

    const theme = useTheme();

    const data = {
        datasets: [
            {
                data: [63, 15, 22],
                backgroundColor: [
                    theme.palette.primary.main,
                    theme.palette.error.main,
                    theme.palette.warning.main
                ],
                borderWidth: 8,
                borderColor: theme.palette.white,
                hoverBorderColor: theme.palette.white
            }
        ],
        labels: ['Desktop', 'Tablet', 'Mobile']
    };

    const options = {
        legend: {
            display: false
        },
        responsive: true,
        maintainAspectRatio: false,
        layout: { padding: 0 },
        tooltips: {
            enabled: true,
            mode: 'index',
            intersect: false,
            borderWidth: 1,
            borderColor: theme.palette.divider,
            backgroundColor: theme.palette.white,
            titleFontColor: theme.palette.text.primary,
            bodyFontColor: theme.palette.text.secondary,
            footerFontColor: theme.palette.text.secondary
        }
    };

    const devices = [
        {
            title: 'Desktop',
            value: '63',
            icon: <LaptopMacIcon />,
            color: theme.palette.primary.main
        },
        {
            title: 'Tablet',
            value: '15',
            icon: <TabletMacIcon />,
            color: theme.palette.error.main
        },
        {
            title: 'Mobile',
            value: '23',
            icon: <PhoneIphoneIcon />,
            color: theme.palette.warning.main
        }
    ];

    const handleClick = event => {
        setAnchorEl(event.currentTarget);
    };
    const handleClose = () => {
        setAnchorEl(null);
    };

    return (
        <Card {...rest} className={clsx(classes.root, className)}>
            <CardHeader
                action={
                    <IconButton edge="end" size="small" onClick={handleClick}>
                        <MoreVertIcon />
                    </IconButton>
                }
                title="Members by presence"
            />
            <Divider />
            <CardContent>
                <div className={classes.chartContainer}>
                    <Pie data={data} options={options} />
                </div>
                <div className={classes.stats}>
                    {devices.map(device => (
                        <div className={classes.device} key={device.title}>
                            <span className={classes.deviceIcon}>
                                {device.icon}
                            </span>
                            <Typography variant="body1">
                                {device.title}
                            </Typography>
                            <Typography
                                style={{ color: device.color }}
                                variant="h2"
                            >
                                {device.value}%
                            </Typography>
                        </div>
                    ))}
                </div>
            </CardContent>
            <Menu
                anchorEl={anchorEl}
                anchorOrigin={{
                    vertical: 'top',
                    horizontal: 'right'
                }}
                keepMounted
                transformOrigin={{
                    vertical: 'top',
                    horizontal: 'right'
                }}
                open={open}
                onClose={handleClose}
            >
                <MenuItem>
                    <Typography variant="button">
                        Choose
                    </Typography>
                </MenuItem>
                <MenuItem>
                    <Typography variant="button">
                        Delete
                    </Typography>
                </MenuItem>
            </Menu>
        </Card>
    );
};
export default withStyles(styles)(MemberByPresence);
