import { SEARCH_TASK, FILTER_TASK, CLEARN_TASK } from './../constants/types';

export const searchTask = keyword => dispatch => {
    dispatch({
        type: SEARCH_TASK,
        payload: keyword
    });
};
export const filterTask = index => dispatch => {
    dispatch({
        type: FILTER_TASK,
        payload: index
    });
};
export const clearnTask = () => dispatch => {
    dispatch({
        type: CLEARN_TASK,
        payload: {}
    });
};
