import React, { useState } from 'react';
import Dialog from '@material-ui/core/Dialog';
import {
    Card,
    CardHeader,
    CardContent,
    CardActions,
    Divider,
    Grid,
    Button,
    TextField
} from '@material-ui/core';
import { withStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Draggable from 'react-draggable';
import DateFnsUtils from '@date-io/date-fns';
import { MuiPickersUtilsProvider, DatePicker } from '@material-ui/pickers';

import styles from './styles';

const PaperComponent = props => {
    return (
        <Draggable cancel={'[class*="MuiDialogContent-root"]'}>
            <Paper {...props} />
        </Draggable>
    );
};
const AddMemberDialog = props => {
    const { open, handleCloseDialog, classes } = props;
    const [value, setValue] = useState({
        mssv: '',
        ten: '',
        gioitinh: '',
        ngaysinh: ''
    });
    const handleClose = () => {
        handleCloseDialog(false);
    };
    return (
        <Dialog
            open={open}
            onClose={handleClose}
            PaperComponent={PaperComponent}
            aria-labelledby="draggable-dialog-title"
        >
            <div className={classes.root}>
                <Card>
                    <form autoComplete="off" noValidate>
                        <CardHeader title="Add member" />
                        <Divider />
                        <CardContent>
                            <Grid container spacing={1}>
                                <MuiPickersUtilsProvider utils={DateFnsUtils}>
                                    <Grid item md={6} xs={12}>
                                        <TextField
                                            fullWidth
                                            label="Member code"
                                            margin="dense"
                                            name="mssv"
                                            variant="outlined"
                                            required
                                            // onChange={handleChange}
                                            // error={errors.tenlop ? true : false}
                                            // helperText={
                                            //     errors.tenlop
                                            //         ? errors.tenlop
                                            //         : null
                                            // }
                                        />
                                    </Grid>
                                    <Grid item md={6} xs={12}>
                                        <TextField
                                            fullWidth
                                            label="Member name"
                                            margin="dense"
                                            name="ten"
                                            required
                                            variant="outlined"
                                            // onChange={handleChange}
                                            // error={errors.mota ? true : false}
                                            // helperText={
                                            //     errors.mota ? errors.mota : null
                                            // }
                                        />
                                    </Grid>
                                    <Grid item md={6} xs={12}>
                                        <TextField
                                            fullWidth
                                            label="Gender"
                                            margin="dense"
                                            name="gioitinh"
                                            required
                                            variant="outlined"
                                            // onChange={handleChange}
                                            // error={errors.mota ? true : false}
                                            // helperText={
                                            //     errors.mota ? errors.mota : null
                                            // }
                                        />
                                    </Grid>
                                    <Grid item md={6} xs={12}>
                                        <DatePicker
                                            inputVariant="outlined"
                                            label="Start date"
                                            format="dd/MM/yyyy"
                                            onChange={handleDateChangeDateStart}
                                            fullWidth
                                            value={selectedDateStart}
                                            minDate={today}
                                        />
                                    </Grid>
                                </MuiPickersUtilsProvider>
                            </Grid>
                        </CardContent>
                        <Divider />
                        <CardActions>
                            <Grid
                                container
                                direction="row"
                                justify="flex-end"
                                alignItems="flex-end"
                            >
                                <Button
                                    type="submit"
                                    color="primary"
                                    // onClick={handleClickAddClass}
                                    // disabled={loadingButton ? true : false}
                                >
                                    Save
                                    {/* {loadingButton ? <LoadingButton /> : null} */}
                                </Button>
                                <Button onClick={handleClose} color="secondary">
                                    Cancel
                                </Button>
                            </Grid>
                        </CardActions>
                    </form>
                </Card>
            </div>
        </Dialog>
    );
};
export default withStyles(styles)(AddMemberDialog);
