import palette from 'theme/palette';
import moment from 'moment';

export const fillData = dataPresences => {
    let data;
    const dayFirstWeek = moment().subtract(1, 'weeks').startOf('isoWeek'),
        dayLastWeek = moment().subtract(1, 'weeks').endOf('isoWeek');
    console.log(dataPresences);

    data = {
        labels: [
            'Mon',
            'Tue',
            'Wed',
            'Thu',
            'Fri',
            'Sat',
            'Sun'
        ],
        datasets: [
            {
                label: 'Present',
                backgroundColor: 'rgba(241, 91, 193, 0.27)',
                data: [6, 82, 22, 31, 21, 73, 20],
                fill: 'origin',
            }
        ]
    };
    return {
        data
    };
};



export const options = {
    responsive: true,
    maintainAspectRatio: false,
    legend: { display: false },
    lineTension: 0,
    tooltips: {
        enabled: true,
        mode: 'index',
        intersect: false,
        borderWidth: 1,
        borderColor: palette.divider,
        backgroundColor: palette.white,
        titleFontColor: palette.text.primary,
        bodyFontColor: palette.text.secondary,
        footerFontColor: palette.text.secondary
    },

    layout: { padding: 0 },
    scales: {
        xAxes: [
            {
                gridLines: {
                    borderDash: [2],
                }
            }
        ],
        yAxes: [
            {
                gridLines: {
                    borderDash: [2],
                    color: palette.divider,
                    zeroLineBorderDash: [2],
                    zeroLineColor: palette.divider
                }
            }
        ]
    }
};
