import React, { useState } from 'react';
import { withStyles } from '@material-ui/core/styles';
import {
    Typography,
    SwipeableDrawer,
    Avatar,
    TablePagination,
    FormControlLabel,
    CircularProgress
} from '@material-ui/core';
import { SearchInput, LoadingLocal } from 'components';
import { URI } from './../../../../constants/types';
import Drawer from '@material-ui/core/Drawer';
import styles from './styles';

const DrawClassAssignes = props => {
    const { classes, open, handleChangePerson } = props;
    const [rowsPerPage, setRowsPerPage] = useState(8);
    const [page, setPage] = useState(0);
    const [loading, setLoading] = useState({ load: '' });

    return (
        <div>
            <Drawer
                anchor="right"
                open={open}
                onClose={handleChangePerson('right', false)}
            >
                <div className={classes.list}>
                    <div className={classes.search}>
                        <SearchInput placeholder="Search person" />
                    </div>
                    <div className={classes.drawHeader}>
                        <Typography variant="h5">Change manager</Typography>
                    </div>
                    {loadingLocal ? (
                        <LoadingLocal size={30} />
                    ) : (
                        users
                            .slice(
                                page * rowsPerPage,
                                page * rowsPerPage + rowsPerPage
                            )
                            .map(user => (
                                <div
                                    className={classes.nameContainer}
                                    key={user._id}
                                >
                                    <FormControlLabel
                                        onClick={event =>
                                            handleClick(event, user._id)
                                        }
                                        control={
                                            <Avatar
                                                className={classes.avatar}
                                                src={`${URI}/getAvatar/${user.avatar}`}
                                            />
                                        }
                                        label={
                                            <Typography variant="h6">
                                                {user.name}
                                            </Typography>
                                        }
                                    />
                                    { loading.load === user._id && <CircularProgress size={20} color='primary' /> }
                                </div>
                            ))
                    )}
                    <div className={classes.pagination}>
                        <TablePagination
                            component="div"
                            count={users.length}
                            onChangePage={handlePageChange}
                            onChangeRowsPerPage={handleRowsPerPageChange}
                            page={page}
                            rowsPerPage={rowsPerPage}
                            rowsPerPageOptions={[]}
                            backIconButtonProps={{
                                'aria-label': 'previous page'
                            }}
                            nextIconButtonProps={{
                                'aria-label': 'next page'
                            }}
                            labelRowsPerPage=""
                        />
                    </div>
                </div>
            </Drawer>
        </div>
    );
};
export default withStyles(styles)(DrawClassAssignes);
