import excel from 'exceljs';

export default data => {
    const workbook = new excel.Workbook();
    workbook.creator = 'Me';
    workbook.lastModifiedBy = 'Me';
    workbook.created = new Date();
    workbook.modified = new Date();

    var sheet1 = workbook.addWorksheet('Sheet1');
    sheet1.mergeCells('A3', 'D4');
    sheet1.getCell('A3').value = 'List member presence';
    sheet1.getCell('A3').alignment = {
        vertical: 'middle',
        horizontal: 'center'
    };
    sheet1.getCell('A3').font = {
        name: 'Comic Sans MS',
        family: 4,
        size: 16,
        underline: true,
        bold: true
    };

    sheet1.mergeCells('A5', 'A6');
    sheet1.getCell('A5').value = 'STT';
    sheet1.mergeCells('B5', 'B6');
    sheet1.getCell('B5').value = 'Ma so sinh vien';
    sheet1.mergeCells('C5', 'C6');
    sheet1.getCell('C5').value = 'Ten sinh vien';
    sheet1.mergeCells('D5', 'D6');
    sheet1.getCell('D5').value = 'Ngay sinh';
    sheet1.mergeCells('E5', 'E6');
    sheet1.getCell('E5').value = 'Gioi tinh';
    sheet1.mergeCells('F5', 'G5');
    sheet1.getCell('F5').value = 'Ngay dien danh';
    sheet1.getCell('F5').alignment = {
        vertical: 'middle',
        horizontal: 'center'
    };

    sheet1.columns = [
        { key: 'stt', width: 5 },
        { key: 'maSV', width: 20 },
        { key: 'tenSV', width: 30 },
        { key: 'ngaysinh', width: 20 },
        { key: 'gioitinh', width: 10 },
        { key: 'checkDate', width: 10 }
    ];

    let stt = 0;
    data.forEach(sv => {
        sheet1.addRow({
            stt: stt + 1,
            maSV: sv.maSV,
            tenSV: sv.tenSV,
            ngaysinh: sv.ngaysinh,
            gioitinh: sv.gioitinh
        });
        stt++;
    });

    workbook.xlsx.writeBuffer().then(function(data) {
        const blob = new Blob([data], {
            type:
                'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet'
        });
        const url = window.URL.createObjectURL(blob);
        const anchor = document.createElement('a');
        anchor.href = url;
        anchor.download = 'download.xlsx';
        anchor.click();
        window.URL.revokeObjectURL(url);
    });
};
