import React from 'react';
import { withStyles } from '@material-ui/styles';
import { Grid } from '@material-ui/core';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';

import { AccountProfile, AccountDetails } from './components';
import { LoadingCenter } from './../../components';
import styles from './styles';
import { getInfoUser } from './../../actions/authentication';
import { uploadPicture } from './../../actions/account';

const Account = props => {
    const { errors, user, classes, file, isAuth, getInfoUser, uploadPicture } = props;

    console.log(user);
    if(!user)
    {
        return <h1>loading.....</h1>;
    }
    return (
        <div className={classes.root}>
            <Grid container spacing={4}>
                <Grid item lg={4} md={6} xl={4} xs={12}>
                    <AccountProfile 
                        errors={errors}
                        user={user}
                        file={file}
                        isAuth={isAuth}
                        getInfoUser={getInfoUser}
                        uploadPicture={uploadPicture}
                    />
                </Grid>
                <Grid item lg={8} md={6} xl={8} xs={12}>
                    <AccountDetails 
                    />
                </Grid>
            </Grid>
        </div>
    );
};
Account.propTypes = {
    errors: PropTypes.object,
    user: PropTypes.object
}
const mapStateToProps = state => ({
    errors: state.errors,
    user: state.auth.user,
    file: state.file,
    isAuth: state.auth.isAuthenticated
});
export default connect(
    mapStateToProps,
    {getInfoUser, uploadPicture}
)(withStyles(styles)(Account));
