import { GET_ERRORS, CLEARN_ERRORS } from './../constants/types';

const initialState = {
    errors: {},
    message: '',
    isSuccess: false
};

export default function(state = initialState, action) {
    switch (action.type) {
        case GET_ERRORS:
            return {
                ...state,
                errors: state.payload.errors,
                message: state.payload.message,
                isSuccess: state.payload.isSuccess
            }
        case CLEARN_ERRORS: 
            return action.payload;
        default: 
            return state;
    }
}
