import React, { Component } from 'react';
import { withStyles } from '@material-ui/styles';
import { connect } from 'react-redux';

import { UsersToolbar, UsersTable } from './components';
import { getUsers, deleteUsers } from './../../actions/users';
import { LoadingCenter, Notifies } from 'components';
import styles from './styles';

class UserList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            showStatus: false
        };
    }
    componentDidMount() {
        this.props.getUsers();
    }
    componentDidUpdate(preProps) {
        if(preProps.users !== this.props.users)
        {
            this.setState({
                showStatus: true
            });
        }
    }
    render() {
        const { classes, users, getUsers, deleteUsers, statusUser } = this.props;
        const { showStatus } = this.state;
        if(users.length === 0 && statusUser !== 'USERS_NOTFOUND')
        { 
            return <LoadingCenter />;
        }
        return (
                <div className={classes.root}>
                    { showStatus ? <Notifies variant="success" message={statusUser} /> : null }
                    <UsersToolbar />
                    <div className={classes.content}>
                        <UsersTable 
                            users={users} 
                            deleteUsers={deleteUsers}
                        />
                    </div>
                </div>
        );
    }
};
const mapStateToProps = state => ({
    users: state.users.users,
    statusUser: state.users.status
});
export default connect(mapStateToProps, { getUsers, deleteUsers })(withStyles(styles)(UserList));
