import React from 'react';
import { withStyles } from '@material-ui/styles';
import { Grid } from '@material-ui/core';
import { connect } from 'react-redux';

import { Notifications, Password } from './components';
import styles from './styles';
import { updatePassword } from './../../actions/account';

const Settings = props => {
    const { classes, updatePassword, user, statusUpdateInfo } = props;
    return (
        <div className={classes.root}>
            <Grid container spacing={4}>
                <Grid item md={7} xs={12}>
                    <Notifications />
                </Grid>
                <Grid item md={5} xs={12}>
                    <Password 
                        user={user}
                        updatePass={updatePassword}
                    />
                </Grid>
            </Grid>
        </div>
    );
};
const mapStateToProps = state => ({
    statusUpdateInfo: state.info.status,
    user: state.auth.user,
});
export default connect(mapStateToProps, { updatePassword })(withStyles(styles)(Settings));
