const styles = theme => ({
    root: {
        width: '50%',
        height: '45%'
    },
    avatar: {
        marginLeft: 'auto',
        height: '100%',
        width: '100%',
        borderRadius: 0,
        flexShrink: 0,
        flexGrow: 0
    },
    uploadButton: {
        marginRight: theme.spacing(2)
    },
    input: {
        display: 'none'
    },
    details: {
        display: 'flex'
    },
    cardStyle: {
        width: '30vw',
        height: '45vw'
    }
});
export default styles;
