import React from 'react';
import moment from 'moment';
import clsx from 'clsx';
import { withStyles } from '@material-ui/styles';
import {
    Card,
    CardHeader,
    CardContent,
    CardActions,
    Typography,
    Grid,
    Divider,
    Avatar,
    Button
} from '@material-ui/core';
import { StatusBullet } from 'components';
import { URI } from './../../../../constants/types';
import styles from './styles';

const statusColors = {
    true: 'success',
    false: 'danger'
};

const PresencesCard = props => {
    const { className, classes, lop } = props;
    console.log(lop);
    return (
        <Card className={clsx(classes.root, className)}>
            <CardHeader
                avatar={
                    <Avatar
                        src={
                            lop.hinhdaidien
                                ? `${URI}/getAvatar/${lop.hinhdaidien}`
                                : 'http://www.gravatar.com/avatar/f8aef9003205946523250a062b54bbb6?s=200&r=pg&d=retro'
                        }
                    />
                }
                title={<Typography variant="h5">{lop.tenlop}</Typography>}
                subheader={
                    <Typography variant="body2">
                        by{' '}
                        <span style={{ fontWeight: 'bold', fontSize: '30' }}>
                            {lop.tenUser}
                        </span>
                    </Typography>
                }
            />
            <CardContent>
                <Grid container justify="space-between" alignItems="center">
                    <Grid item>
                        <Grid
                            container
                            direction="column"
                            justify="center"
                            spacing={2}
                        >
                            <Grid item>
                                <Typography variant="h6">
                                    {moment(lop.thoigianketthuc).fromNow()}
                                </Typography>
                                <Typography variant="body2">
                                    End Time
                                </Typography>
                            </Grid>
                            <Grid item>
                                <Typography variant="h6">
                                    {moment(lop.thoigianbatdau).format(
                                        'DD/MM/YYYY'
                                    )}
                                </Typography>
                                <Typography variant="body2">
                                    Start date
                                </Typography>
                            </Grid>
                            <Grid item>
                                <div className={classes.statusContainer}>
                                    <StatusBullet
                                        className={classes.status}
                                        color={statusColors[lop.status]}
                                        size="sm"
                                    />
                                    {lop.status ? 'Active' : 'Inactive'}
                                </div>
                                <Typography variant="body2">Status</Typography>
                            </Grid>
                        </Grid>
                    </Grid>
                    <Grid item>
                        <Grid
                            container
                            direction="column"
                            justify="center"
                            spacing={2}
                        >
                            <Grid item>
                                <Typography variant="h6">12</Typography>
                                <Typography variant="body2">
                                    Total member
                                </Typography>
                            </Grid>
                            <Grid item>
                                <Typography variant="h6">
                                    12 November 2019
                                </Typography>
                                <Typography variant="body2">
                                    End time
                                </Typography>
                            </Grid>
                            <Grid item>
                                <Button color="primary" variant="outlined">
                                    View
                                </Button>
                            </Grid>
                        </Grid>
                    </Grid>
                </Grid>
            </CardContent>
        </Card>
    );
};
export default withStyles(styles)(PresencesCard);
