import React from 'react';
import PropTypes from 'prop-types';
import clsx from 'clsx';
import { withStyles } from '@material-ui/styles';
import {
    Card,
    CardContent,
    CardActions,
    Typography,
    Grid,
    Divider
} from '@material-ui/core';
import AccessTimeIcon from '@material-ui/icons/AccessTime';
import moment from 'moment';
import { StatusBullet } from 'components';
import { truncateString } from 'common';
import { URI } from './../../../../constants/types';
import styles from './styles';

const statusColors = {
    true: 'success',
    false: 'danger'
};
const ProductCard = props => {
    const { className, classes, lop } = props;
    return (
        <Card className={clsx(classes.root, className)}>
            <CardContent>
                <div className={classes.imageContainer}>
                    <img
                        alt="class"
                        className={classes.image}
                        src={
                            lop.hinhdaidien
                                ? `${URI}/getAvatar/${lop.hinhdaidien}`
                                : 'http://www.gravatar.com/avatar/f8aef9003205946523250a062b54bbb6?s=200&r=pg&d=retro'
                        }
                    />
                </div>
                <Typography align="center" gutterBottom variant="h4">
                    {truncateString(lop.tenlop, 30, true)}
                </Typography>
                <Typography align="center" variant="h6">
                    {lop.malop}
                </Typography>
                <Typography align="center" variant="body1">
                    {truncateString(lop.mota, 45, true)}
                </Typography>
            </CardContent>
            <Divider />
            <CardActions>
                <Grid container justify="space-between">
                    <Grid>
                        <Grid className={classes.statsItem} item>
                            <AccessTimeIcon className={classes.statsIcon} />
                            <Typography display="inline" variant="body2">
                                {`Created ${moment(lop.createdAt).fromNow()}`}
                            </Typography>
                        </Grid>
                        <Grid className={classes.statsItem} item>
                            <AccessTimeIcon className={classes.statsIcon} />
                            <Typography display="inline" variant="body2">
                                {`Updated ${moment(lop.updatedAt).fromNow()}`}
                            </Typography>
                        </Grid>
                    </Grid>
                    <Grid>
                    <Grid className={classes.statsItem} item>
                            <div className={classes.statusContainer}>
                                <Typography display="inline" variant="body2">
                                    <StatusBullet
                                        className={classes.status}
                                        color={statusColors[lop.status]}
                                        size="sm"
                                    />
                                    {lop.status === true
                                        ? 'Active'
                                        : 'Inactive'}
                                </Typography>
                            </div>
                        </Grid>
                        <Grid className={classes.statsItem} item>
                            <div className={classes.statusContainer}>
                                <Typography display="inline" variant="body2">
                                    <StatusBullet
                                        className={classes.status}
                                        color={statusColors[lop.managed]}
                                        size="sm"
                                    />
                                    {lop.managed === true
                                        ? 'Managed'
                                        : 'Not yet managed'}
                                </Typography>
                            </div>
                        </Grid>
                    </Grid>
                </Grid>
            </CardActions>
        </Card>
    );
};

ProductCard.propTypes = {
    className: PropTypes.string
};

export default withStyles(styles)(ProductCard);
