import React, { useState } from 'react';
import Dialog from '@material-ui/core/Dialog';
import {
    Card,
    CardHeader,
    CardContent,
    CardActions,
    Divider,
    Grid,
    Button,
    TextField,
    Radio,
    RadioGroup,
    FormControlLabel,
    FormControl,
    FormLabel
} from '@material-ui/core';
import { withStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Draggable from 'react-draggable';
import DateFnsUtils from '@date-io/date-fns';
import { MuiPickersUtilsProvider, DatePicker } from '@material-ui/pickers';
import moment from 'moment';

import styles from './styles';

const PaperComponent = props => {
    return (
        <Draggable cancel={'[class*="MuiDialogContent-root"]'}>
            <Paper {...props} />
        </Draggable>
    );
};
const AddMemberDialog = props => {
    const { open, handleCloseDialog, classes } = props;
    const [value, setValue] = useState({
        mssv: '',
        ten: ''
    });
    const [ngaysinh, setNgaysinh] = useState(new Date());
    const today = moment();
    const handleDateChangeDate = date => {
        setNgaysinh(date);
    };
    const handleChange = e => {
        setValue({
            ...value,
            [e.target.name]: e.target.value
        });
    };
    const handleClose = () => {
        handleCloseDialog(false);
    };
    const handleClick = e => {
        e.preventDefault();
        console.log(ngaysinh);
        console.log(value);
    };
    return (
        <Dialog
            open={open}
            onClose={handleClose}
            PaperComponent={PaperComponent}
            aria-labelledby="draggable-dialog-title"
        >
            <div className={classes.root}>
                <Card>
                    <form autoComplete="off" noValidate>
                        <CardHeader title="Add member" />
                        <Divider />
                        <CardContent>
                            <Grid container spacing={1}>
                                <MuiPickersUtilsProvider utils={DateFnsUtils}>
                                    <Grid item md={6} xs={12}>
                                        <TextField
                                            fullWidth
                                            label="Member code"
                                            margin="dense"
                                            name="mssv"
                                            variant="outlined"
                                            required
                                            onChange={handleChange}
                                            // error={errors.tenlop ? true : false}
                                            // helperText={
                                            //     errors.tenlop
                                            //         ? errors.tenlop
                                            //         : null
                                            // }
                                        />
                                    </Grid>
                                    <Grid item md={6} xs={12}>
                                        <TextField
                                            fullWidth
                                            label="Member name"
                                            margin="dense"
                                            name="ten"
                                            required
                                            variant="outlined"
                                            onChange={handleChange}
                                            // error={errors.mota ? true : false}
                                            // helperText={
                                            //     errors.mota ? errors.mota : null
                                            // }
                                        />
                                    </Grid>
                                    <Grid item md={6} xs={12}>
                                        <DatePicker
                                            inputVariant="outlined"
                                            label="Birth day"
                                            format="dd/MM/yyyy"
                                            onChange={handleDateChangeDate}
                                            fullWidth
                                            value={ngaysinh}
                                            maxDate={today}
                                            margin="dense"
                                        />
                                    </Grid>
                                    <Grid item md={6} xs={12}>
                                        <FormControl component="fieldset">
                                            <FormLabel component="legend">
                                                Gender
                                            </FormLabel>
                                            <RadioGroup
                                                aria-label="gioitinh"
                                                name="gioitinh"
                                                value={value.gioitinh}
                                                //onChange={handleChange}
                                                row
                                            >
                                                <FormControlLabel
                                                    value="male"
                                                    control={
                                                        <Radio color="secondary" />
                                                    }
                                                    label="Male"
                                                    labelPlacement="start"
                                                />
                                                <FormControlLabel
                                                    value="female"
                                                    control={
                                                        <Radio color="secondary" />
                                                    }
                                                    label="Female"
                                                    labelPlacement="start"
                                                />
                                            </RadioGroup>
                                        </FormControl>
                                    </Grid>
                                </MuiPickersUtilsProvider>
                            </Grid>
                        </CardContent>
                        <Divider />
                        <CardActions>
                            <Grid
                                container
                                direction="row"
                                justify="flex-end"
                                alignItems="flex-end"
                            >
                                <Button
                                    type="submit"
                                    color="primary"
                                    onClick={handleClick}
                                    // disabled={loadingButton ? true : false}
                                >
                                    Save
                                    {/* {loadingButton ? <LoadingButton /> : null} */}
                                </Button>
                                <Button onClick={handleClose} color="secondary">
                                    Cancel
                                </Button>
                            </Grid>
                        </CardActions>
                    </form>
                </Card>
            </div>
        </Dialog>
    );
};
export default withStyles(styles)(AddMemberDialog);
