import React, { useState } from 'react';
import clsx from 'clsx';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/styles';
import {
    Card,
    CardHeader,
    CardContent,
    CardActions,
    Button,
    Divider,
    List,
    ListItem,
    ListItemAvatar,
    ListItemText,
    IconButton,
    Menu,
    MenuItem,
    Typography
} from '@material-ui/core';
import ArrowRightIcon from '@material-ui/icons/ArrowRight';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import { Link as LinkClasses } from 'react-router-dom';

import styles from './styles';
import mockData from './data';

const LatestClasses = props => {
    const { className } = props;

    const [products] = useState(mockData);
    const [anchorEl, setAnchorEl] = React.useState(null);
    const open = Boolean(anchorEl);

    function handleClick(event) {
        setAnchorEl(event.currentTarget);
    }

    function handleClose() {
        setAnchorEl(null);
    }
    return (
        <Card className={clsx(classes.root, className)}>
            <CardHeader
                title="Latest classes"
            />
            <Divider />
            <CardContent className={classes.content}>
                <List>
                    {products.map((product, i) => (
                        <ListItem
                            divider={i < products.length - 1}
                            key={product.id}
                        >
                            <ListItemAvatar>
                                <img
                                    alt="Product"
                                    className={classes.image}
                                    src={product.imageUrl}
                                />
                            </ListItemAvatar>
                            <ListItemText
                                primary={product.name}
                                secondary={`Updated ${product.updatedAt.fromNow()}`}
                            />
                            <IconButton
                                edge="end"
                                size="small"
                                onClick={handleClick}
                            >
                                <MoreVertIcon />
                            </IconButton>
                            <Menu
                                anchorEl={anchorEl}
                                anchorOrigin={{
                                    vertical: 'top',
                                    horizontal: 'right'
                                }}
                                keepMounted
                                transformOrigin={{
                                    vertical: 'top',
                                    horizontal: 'right'
                                }}
                                open={open}
                                onClose={handleClose}
                            >
                                <MenuItem onClick={handleClose}>
                                    <Typography variant="button">View detail</Typography>
                                </MenuItem>
                                <MenuItem onClick={handleClose}>
                                    <Typography variant="button">Edit</Typography>
                                </MenuItem>
                                <MenuItem onClick={handleClose}>
                                    <Typography variant="button">Delete</Typography>
                                </MenuItem>
                            </Menu>
                        </ListItem>
                    ))}
                </List>
            </CardContent>
            <Divider />
            <CardActions className={classes.actions}>
                <LinkClasses to="/classes">
                    <Button color="primary" size="small" variant="text">
                        View all <ArrowRightIcon />
                    </Button>
                </LinkClasses>
            </CardActions>
        </Card>
    );
};
export default withStyles(styles)(LatestClasses);
