import React, { useState } from 'react';
import clsx from 'clsx';
import { withStyles } from '@material-ui/core/styles';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import IconButton from '@material-ui/core/IconButton';
import Tooltip from '@material-ui/core/Tooltip';
import DeleteIcon from '@material-ui/icons/Delete';
import EditIcon from '@material-ui/icons/Edit';

import EditMemberDialog from './../EditMemberDialog/EditMemberDialog';
import styles from './styles';

const getMember = (dssv, idMember) => {
    const member = {};
    dssv.forEach(sv => {
        if(sv._id === idMember)
        {
            member.maSV = sv.maSV;
            member.tenSV = sv.tenSV;
            member.ngaysinh = sv.ngaysinh;
            member.gioitinh = sv.gioitinh;
            return false;
        }
    });
    return member;
};

const TableToolBar = props => {
    const { numSelected, classes, selectedMember, dssv } = props;
    const [open, setOpen] = useState(false);
    const [member] = useState({});

    const handleClickEdit = () => {
        setOpen(true);
        member = getMember(dssv, selectedMember[0]);
    };
    const handleCloseDialog = params => {
        setOpen(params);
    };

    return (
        <React.Fragment>
            <EditMemberDialog 
                open={open}
                handleCloseDialog={handleCloseDialog}
                member={member}
            />
            <Toolbar
                className={clsx(classes.root, {
                    [classes.highlight]: numSelected > 0
                })}
            >
                {numSelected > 0 && (
                    <Typography
                        className={classes.title}
                        color="inherit"
                        variant="subtitle1"
                    >
                        {numSelected} selected
                    </Typography>
                )}

                {numSelected === 1 && (
                    <Tooltip title="Edit">
                        <IconButton aria-label="edit" onClick={handleClickEdit}>
                            <EditIcon color="primary" />
                        </IconButton>
                    </Tooltip>
                )}

                {numSelected > 0 && (
                    <Tooltip title="Delete">
                        <IconButton aria-label="delete">
                            <DeleteIcon color="secondary" />
                        </IconButton>
                    </Tooltip>
                )}
            </Toolbar>
        </React.Fragment>
    );
};
export default withStyles(styles)(TableToolBar);
