import { UPDATE_INFO } from './../constants/types';

const initialState = {
    user: {},
    status: ''
};

export default function(state = initialState, action) {
    switch(action.type) {
        case UPDATE_INFO:
            return action.payload;
        default:
            return state;   
    }
};
