import React, { useState } from 'react';
import clsx from 'clsx';
import { withStyles } from '@material-ui/styles';
import {
    Card,
    CardHeader,
    CardContent,
    CardActions,
    Button,
    Divider,
    List,
    ListItem,
    ListItemAvatar,
    ListItemText,
    IconButton,
    Menu,
    MenuItem,
    Typography
} from '@material-ui/core';
import ArrowRightIcon from '@material-ui/icons/ArrowRight';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import { Link as LinkClasses } from 'react-router-dom';
import { URI } from './../.../../../../../constants/types';
import styles from './styles';

const LatestClasses = props => {
    const { className, classes, classLatest } = props;

    const [anchorEl, setAnchorEl] = React.useState(null);
    const open = Boolean(anchorEl);

    function handleClick(event) {
        setAnchorEl(event.currentTarget);
    }

    function handleClose() {
        setAnchorEl(null);
    }
    return (
        <Card className={clsx(classes.root, className)}>
            <CardHeader title="Latest classes" />
            <Divider />
            <CardContent className={classes.content}>
                <List>
                    {classLatest.map((lop, i) => (
                        <ListItem
                            divider={i < classLatest.length - 1}
                            key={lop._id}
                        >
                            <ListItemAvatar>
                                <img
                                    alt="ClassImg"
                                    className={classes.image}
                                    src={
                                        lop.hinhdaidien
                                            ? `${URI}/getAvatar/${lop.hinhdaidien}`
                                            : 'http://www.gravatar.com/avatar/f8aef9003205946523250a062b54bbb6?s=200&r=pg&d=retro'
                                    }
                                />
                            </ListItemAvatar>
                            <ListItemText
                                primary={lop.tenlop}
                                secondary={`Created ${lop.createdAt.fromNow()}`}
                            />
                            <IconButton
                                edge="end"
                                size="small"
                                onClick={handleClick}
                            >
                                <MoreVertIcon />
                            </IconButton>
                            <Menu
                                anchorEl={anchorEl}
                                anchorOrigin={{
                                    vertical: 'top',
                                    horizontal: 'right'
                                }}
                                keepMounted
                                transformOrigin={{
                                    vertical: 'top',
                                    horizontal: 'right'
                                }}
                                open={open}
                                onClose={handleClose}
                            >
                                <MenuItem onClick={handleClose}>
                                    <Typography variant="button">
                                        View detail
                                    </Typography>
                                </MenuItem>
                                <MenuItem onClick={handleClose}>
                                    <Typography variant="button">
                                        Edit
                                    </Typography>
                                </MenuItem>
                                <MenuItem onClick={handleClose}>
                                    <Typography variant="button">
                                        Delete
                                    </Typography>
                                </MenuItem>
                            </Menu>
                        </ListItem>
                    ))}
                </List>
            </CardContent>
            <Divider />
            <CardActions className={classes.actions}>
                <LinkClasses to="/classes">
                    <Button color="primary" size="small" variant="text">
                        View all <ArrowRightIcon />
                    </Button>
                </LinkClasses>
            </CardActions>
        </Card>
    );
};
export default withStyles(styles)(LatestClasses);
