const styles = theme => ({
    root: {},
    avatar: {
        marginLeft: 'auto',
        height: '100px',
        width: '100%',
        borderRadius: 0,
        flexShrink: 0,
        flexGrow: 0
    },
    uploadButton: {
        marginRight: theme.spacing(2)
    },
    input: {
        display: 'none'
    },
    details: {
        display: 'flex'
    }
});
export default styles;
