import axios from 'axios';
//import setAuthToken from './setAuthToken';

const URI = 'http://localhost:4000';
export default function callAPI(endPoint, method = 'GET', body) {
    return axios({
        //header: setAuthToken,
        method,
        url: `${URI}${endPoint}`,
        data: body
    })
}
