import React, { Component } from 'react';
import PropTypes from 'prop-types';
import clsx from 'clsx';
import moment from 'moment';
import { makeStyles } from '@material-ui/styles';
import { connect } from 'react-redux';
import {
    Card,
    CardActions,
    CardContent,
    Avatar,
    Typography,
    Divider,
    Button,
    LinearProgress
} from '@material-ui/core';
import { URL_getImage } from './../../../../constants/types';
import { getInfoUser } from './../../../../actions/authentication';
import { uploadPicture } from './../../../../actions/account';

const useStyles = makeStyles(theme => ({
    root: {},
    details: {
        display: 'flex'
    },
    avatar: {
        marginLeft: 'auto',
        height: 110,
        width: 100,
        flexShrink: 0,
        flexGrow: 0
    },
    progress: {
        marginTop: theme.spacing(2)
    },
    uploadButton: {
        marginRight: theme.spacing(2)
    },
    input: {
        display: 'none'
    }
}));

class AccountProfile extends Component {
    state = {
        avatar: ''
    }
    profileComplete = (user, isAuth) => {
        if (isAuth) {
            let complete = 0;
            if (user.firstName) {
                complete += 20;
            }
            if (user.lastName) {
                complete += 20;
            }
            if (user.sdt) {
                complete += 20;
            }
            if (user.city) {
                complete += 20;
            }
            // if (user.avatar.slice(0, 18) !== '//www.gravatar.com') {
            //     complete += 20;
            // }
            return complete;
        } else {
            return 0;
        }
    };
    handleChange = async (e, user) => {
        this.state.avatar = e.target.files[0];
        if (this.state.avatar) {
            const rs = await uploadPicture(this.state.avatar, user.id);
            if (rs === undefined) {
                await getInfoUser(user.id);
            }
        }
    };
    render() {
        const classes = useStyles();
        const {
            className,
            user,
            uploadPicture,
            isAuth,
            getInfoUser,
            file
        } = this.props;
        return (
            <Card className={clsx(classes.root, className)}>
                <CardContent>
                    <div className={classes.details}>
                        <div>
                            <Typography gutterBottom variant="h2">
                                {`${user.firstName} ${user.lastName}`}
                            </Typography>
                            <Typography
                                className={classes.locationText}
                                color="textSecondary"
                                variant="body1"
                            >
                                {user.city}, {user.country}
                            </Typography>
                            <Typography
                                className={classes.dateText}
                                color="textSecondary"
                                variant="body1"
                            >
                                {moment().format('hh:mm A')} ({'GTM-7'})
                            </Typography>
                        </div>
                        <Avatar
                            className={classes.avatar}
                            src={`${URL_getImage}${user.avatar}`}
                        />
                    </div>
                    <div className={classes.progress}>
                        <Typography variant="body1">
                            {`Profile Completeness: ${this.profileComplete(user, isAuth)}%`}
                        </Typography>
                        <LinearProgress
                            value={profileComplete()}
                            variant="determinate"
                        />
                    </div>
                </CardContent>
                <Divider />
                <CardActions>
                    <input
                        accept="image/*"
                        className={classes.input}
                        id="text-button-file"
                        multiple
                        type="file"
                        onChange={(e, user) => this.handleChange(e, user)}
                    />
                    <label htmlFor="text-button-file">
                        <Button
                            component="span"
                            className={classes.uploadButton}
                            color="primary"
                            variant="text"
                        >
                            Upload picture
                        </Button>
                    </label>
                    <Button variant="text">Remove picture</Button>
                </CardActions>
            </Card>
        );
    }
}
