        'Wed',
        'Thu',
        'Fri',
        'Sat',
        'Sun'
    ],
    datasets: [
        {
            label: 'Present',
            backgroundColor: palette.secondary.light,
            data: [10, 5, 19, 27, 29, 19, 20]
        }
    ]
};

