import React from 'react';
import { connect } from 'react-redux';
import palette from 'theme/palette';
import PresentIcon from '@material-ui/icons/Spellcheck';
import ResonIcon from '@material-ui/icons/Warning';
import AbsentIcon from '@material-ui/icons/CancelPresentation';

const dataChart = props => {
    const { presencesData } = props;
    return {
        data: {
            datasets: [
                {
                    data: [80, 5, 15],
                    backgroundColor: [
                        palette.primary.main,
                        palette.error.main,
                        palette.warning.main
                    ],
                    borderWidth: 5,
                    borderColor: palette.white,
                    hoverBorderColor: palette.white
                }
            ],
            labels: ['Present', 'Absent', 'Reason']
        },
        options: {
            legend: {
                display: false
            },
            responsive: true,
            maintainAspectRatio: false,
            layout: { padding: 0 },
            tooltips: {
                enabled: true,
                mode: 'index',
                intersect: false,
                borderWidth: 1,
                borderColor: palette.divider,
                backgroundColor: palette.white,
                titleFontColor: palette.text.primary,
                bodyFontColor: palette.text.secondary,
                footerFontColor: palette.text.secondary
            }
        },
        devices: [
            {
                title: 'Present',
                value: '63',
                icon: <PresentIcon />,
                color: palette.primary.main
            },
            {
                title: 'Absent',
                value: '15',
                icon: <AbsentIcon />,
                color: palette.error.main
            },
            {
                title: 'Reson',
                value: '23',
                icon: <ResonIcon />,
                color: palette.warning.main
            }
        ]
    };
};
const mapStateToProps = state => ({
    presencesData: state.presencesClass
});
export default connect(
    mapStateToProps,
    null
)(dataChart);

