import { UPLOAD_PICTURE, GET_ERRORS, UPDATE_INFO } from './../constants/types';
import callAPI from './../common/callApi';

export const uploadPicture = (file, idUser) => dispatch => {
    const formData = new FormData();
    formData.set('idUser', idUser);
    formData.append('avatar', file);
    callAPI('/updateAvatar', 'POST', formData)
        .then(res => {
            const filePath = res.data.filePath;
            const fileName= filePath.split('/')[1];
            dispatch({
                type: UPLOAD_PICTURE,
                payload: fileName
            });
        })
        .catch(err => {
            dispatch({
                type: GET_ERRORS,
                payload: err.response.data
            });
        });
};
export const updateInfo = user => dispatch => {
    callAPI('/updateInfo', 'PUT', user)
        .then(res => {
            dispatch({
                type: UPDATE_INFO,
                payload: res.data
            });
        })
        .catch(err => {
            dispatch({
                type: GET_ERRORS,
                payload: err.response.data
            });
        });
};
