import React, { Component } from 'react';
import clsx from 'clsx';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/styles';
import {
    Card,
    CardHeader,
    CardContent,
    CardActions,
    Divider,
    Grid,
    Button,
    TextField
} from '@material-ui/core';
import { connect } from 'react-redux';
import styles from './styles';

class AccountDetails extends Component {
    constructor(props)
    {
        super(props);
        this.state = this.props.user
    }     
    handleChange = event => {
        this.setState({
            [event.target.name]: event.target.value
        });
    }
    render() {
        const { className, classes, user } = this.props;
        return (
                <Card className={clsx(classes.root, className)}>
                    <form autoComplete="off" noValidate>
                        <CardHeader
                            subheader="The information can be edited"
                            title="Profile"
                        />
                        <Divider />
                        <CardContent>
                            <Grid container spacing={3}>
                                <Grid item md={6} xs={12}>
                                    <TextField
                                        fullWidth
                                        label="First name"
                                        margin="dense"
                                        name="firstName"
                                        onChange={this.handleChange}
                                        required
                                        value={user.firstName}
                                        variant="outlined"
                                    />
                                </Grid>
                                <Grid item md={6} xs={12}>
                                    <TextField
                                        fullWidth
                                        label="Last name"
                                        margin="dense"
                                        name="lastName"
                                        onChange={this.handleChange}
                                        required
                                        defaultValue={user.lastName}
                                        variant="outlined"
                                    />
                                </Grid>
                                <Grid item md={6} xs={12}>
                                    <TextField
                                        fullWidth
                                        label="Email Address"
                                        margin="dense"
                                        name="email"
                                        onChange={this.handleChange}
                                        required
                                        defaultValue={user.email}
                                        variant="outlined"
                                    />
                                </Grid>
                                <Grid item md={6} xs={12}>
                                    <TextField
                                        fullWidth
                                        label="Phone Number"
                                        margin="dense"
                                        name="sdt"
                                        onChange={this.handleChange}
                                        type="number"
                                        defaultValue={user.sdt}
                                        variant="outlined"
                                    />
                                </Grid>
                                <Grid item md={6} xs={12}>
                                    <TextField
                                        fullWidth
                                        label="Select City"
                                        margin="dense"
                                        name="city"
                                        onChange={this.handleChange}
                                        required
                                        select
                                        SelectProps={{ native: true }}
                                        defaultValue={user.city}
                                        variant="outlined"
                                        //disabled={(citys.length > 0) ? false : true}
                                    >
                                        {/* {citys.map(option => (
                                            <option
                                                key={option._id}
                                                value={option.city}
                                            >
                                                {option.city}
                                            </option>
                                        ))} */}
                                    </TextField>
                                </Grid>
                                <Grid item md={6} xs={12}>
                                    <TextField
                                        fullWidth
                                        label="Country"
                                        margin="dense"
                                        name="country"
                                        onChange={this.handleChange}
                                        required
                                        defaultValue={user.country}
                                        variant="outlined"
                                        disabled
                                    />
                                </Grid>
                            </Grid>
                        </CardContent>
                        <Divider />
                        <CardActions>
                            <Button color="primary" variant="contained">
                                Save details
                            </Button>
                        </CardActions>
                    </form>
                </Card>
        );
    }
}
AccountDetails.propTypes = {
    className: PropTypes.string,
    classes: PropTypes.object,
    user: PropTypes.object
};
const mapStateToProps = state => ({
    user: state.auth.user
});
export default connect(mapStateToProps, null)(withStyles(styles)(AccountDetails));
